import { VNode } from 'snabbdom/vnode';
import { PartialObserver } from 'rxjs/Observer';
import { Subscription } from 'rxjs/Subscription';

import { Type, Injector, ProviderArg } from '../di';
import { 
  Observable, 
  Subject, 
  Cancellable,
  EventBus,
  BusEvent
} from '../events';
import { negate, uid, isNumber, get } from '../utils';
import { ContainerRef, RenderableArg } from '../common';
import { RenderableArea } from './RenderableArea';
import { Renderer } from './Renderer';
import { INJECTOR_KEY, RenderableInjector } from './RenderableInjector';
import { ConfiguredRenderable } from './ConfiguredRenderable';

export interface BaseModificationArgs {
  /**
   * Whether to invoke the render cycle. This is useful for
   * delaying the render cycle to a later time.
   * @type {boolean}
   */
  render?: boolean;
}

/**
 * Args used when removing a child renderable.
 * @export
 * @interface RemoveChildArgs
 * @extends {BaseModificationArgs}
 */
export interface RemoveChildArgs extends BaseModificationArgs {
  /**
   * Whether to destroy the child being removed.
   * @type {boolean}
   */
  destroy?: boolean;
}

/**
 * Args used when adding a child renderable.
 * @export
 * @interface AddChildArgs
 * @extends {BaseModificationArgs}
 */
export interface AddChildArgs extends BaseModificationArgs {
  /**
   * What index to add the child renderable to. If not provided then
   * it will be pushed.
   * @type {number}
   */
  index?: number;
  /**
   * Whether to invoke a resize of this renderable.
   * @type {boolean}
   */
  resize?: boolean;
}

/**
 * The base renderable that all other renderables extend from.
 * @export
 * @abstract
 * @class Renderable
 */
export abstract class Renderable {
  /**
   * Notifies when this renderable is destroyed.
   * @type {Observable<this>}
   */
  destroyed: Observable<this>;
  /**
   * Notifies when the container of this renderable changes.
   * @type {(Observable<Renderable|null>)}
   */
  containerChange: Observable<Renderable|null>;

  protected _eventBus = new EventBus();
  protected _width: number;  
  protected _height: number;
  protected _isDestroyed: boolean = false;
  protected _destroyed: Subject<this> = new Subject();
  protected _uid: number = uid();
  protected _container: Renderable|null;
  protected _containerChange: Subject<Renderable|null> = new Subject<Renderable|null>();
  protected _contentItems: Renderable[] = [];
  protected _renderer: Renderer;

  constructor() {
    this.destroyed = this._destroyed.asObservable();
    this.containerChange = this._containerChange.asObservable();
  }

  /**
   * This renderables container or null if none.
   * @readonly
   * @type {(Renderable|null)}
   */
  get container(): Renderable|null {
    return this._container || null;
  }

  /**
   * This renderables width in pixels.
   * @readonly
   * @type {number}
   */
  get width(): number {
    return this._width;
  }

  /**
   * This renderables height in pixels.
   * @readonly
   * @type {number}
   */
  get height(): number {
    return this._height;
  }

  /**
   * This renderables page offset x.
   * @readonly
   * @type {number}
   */
  get offsetX(): number {
    return get(this._container, 'offsetX', 0, negate(isNumber));
  }

  /**
   * This renderables page offset y.
   * @readonly
   * @type {number}
   */
  get offsetY(): number {
    return get(this._container, 'offsetY', 0, negate(isNumber));
  }

  /**
   * Whether this renderable is destroyed.
   * @readonly
   * @type {boolean}
   */
  get isDestroyed(): boolean {
    return this._isDestroyed;
  }

  /**
   * A unique identifier for this renderable.
   * @readonly
   * @type {number}
   */
  get uid(): number {
    return this._uid;
  }

  /**
   * The amount of content items belonging to this renderable.
   * @readonly
   * @type {number}
   */
  get length(): number {
    return this._contentItems.length;
  }

  /**
   * The injector used to create this renderable.
   * @readonly
   * @type {Injector}
   */
  get injector(): Injector {
    if (!this[INJECTOR_KEY]) {
      throw new Error('Trying to access injector before it is set. Did you create his renderable through a RenderableInjector?');
    }

    return this[INJECTOR_KEY];
  }

  /**
   * Creates this renderables VNode for diffing against the previous VNode state.
   * @abstract
   * @returns {VNode} 
   */
  abstract render(): VNode;

  /**
   * Invoked when the Injector has been assigned and ready for use.
   */
  initialize(): void {
    this._renderer = this.injector.get(Renderer);
    this.setContainer(this.injector.get(ContainerRef, null));
  }

  /**
   * Sets this components size and triggers it's childrens sizing.
   */
  resize(): void {
    for (const child of this.getChildren()) {
      child.resize();
    }
  }

  /**
   * Returns this renderables children renderables. This differs
   * from content items as children should contain all renderables
   * we want as part of the render cycle.
   * @returns {Renderable[]} 
   */
  getChildren(): Renderable[] {
    return [ ...this._contentItems ];
  }

  /**
   * Determines whether this renderable is visible.
   * @returns {boolean} 
   */
  isVisible(): boolean {
    return Boolean(this._container && this._container.isVisible());
  }

  /**
   * Destroys this renderable and all it's children.
   * @returns {void} 
   */
  destroy(): void {
    if (this._isDestroyed) {
      return;
    }

    for (const item of this._contentItems) {
      item.destroy();  
    }

    this._isDestroyed = true;
    this._destroyed.next(this);
    this._destroyed.complete();
  }

  /**
   * Gets this renderables parent or any parent that is
   * an instance of the passed in constructor. If non is found
   * then null is returned.
   * @template T The constructor type.
   * @param {Type<T>} [Ctor] 
   * @returns {(T|null)} 
   */
  getParent<T extends Renderable>(Ctor?: Type<T>): T|null {
    if (this._container) {
      if (!Ctor) {
        return this._container as T;
      }

      if (this._container instanceof Ctor) {
        return this._container as T;
      }

      return this._container.getParent(Ctor);
    }  

    return null;
  }

  /**
   * Gets this renderables parents or any parents that are
   * an instance of the passed in constructor.    
   * @template T The constructor type.
   * @param {Type<T>} [Ctor] 
   * @returns {(T|null)} 
   */
  getParents<T extends Renderable>(Ctor?: Type<T>): T[] {
    let parent: Renderable|null = this;
    let result: T[] = [];

    while (parent = parent.getParent(Ctor)) {
      result.push(parent as T);
    }

    return result;
  }

  /**
   * Sets the container of this renderable.
   * @param {(Renderable|null)} container 
   */
  setContainer(container: Renderable|null): void {
    this._container = container;

    if (this._container) {
      this.injector.setParent(this._container.injector);
    }

    this._containerChange.next(container);
  }

  /**
   * Subscribes to a BusEvent.
   * @template T The event type.
   * @param {Type<T>} Event 
   * @param {PartialObserver<T>|function(event: T)} observer 
   * @returns {Subscription} 
   */
  subscribe<T extends BusEvent<any>>(Event: Type<T>, observer: PartialObserver<T>|((event: T) => void)): Subscription {
    return this._eventBus.subscribe(Event, observer);
  }

  /**
   * Emits a BusEvent on this renderable.
   * @template T The event type.
   * @param {T} event 
   */
  emit<T extends BusEvent<any>>(event: T): void {
    this._eventBus.next(event);
  }

  /**
   * Emits a BusEvent down to all descendants recursively.
   * Propagation can be stopped by any descending renderable.
   * @template T The event type.
   * @param {T} event 
   */
  emitDown<T extends BusEvent<any>>(event: T): void {
    for (const child of this.getDescendants()) {
      if (event.isPropagationStopped) {
        break;
      }

      child.emit(event);
    }
  }

  /**
   * Emits a BusEvent up to all parents recursively.
   * Propagation can be stopped by any parent renderable.
   * @template T The event type.
   * @param {T} event 
   */
  emitUp<T extends BusEvent<any>>(event: T): void {
    for (const parent of this.getParents()) {
      if (event.isPropagationStopped) {
        break;
      }

      parent.emit(event);
    }
  }

  /**
   * Gets all descendants of this renderable recursively.
   * @returns {Renderable[]} 
   */
  getDescendants(): Renderable[] {
    const children = this.getChildren();

    return children.reduce((result, child) => {
      return [ ...result, ...child.getDescendants() ]
    }, children);
  }

  /**
   * Replaces a content item on this renderable with another content item.
   * @param {Renderable} item 
   * @param {Renderable} withItem 
   * @param {RemoveChildArgs} [options={}] 
   */
  replaceChild(item: Renderable, withItem: Renderable, options: RemoveChildArgs = {}): void {
    const { destroy = false, render = true } = options;
    const index = this._contentItems.indexOf(item);

    if (index !== -1) {
      if (destroy) {
        item.destroy();
      }

      this._contentItems.splice(index, 1, withItem);
      withItem.setContainer(this);
      this.resize();

      if (render) {
        this._renderer.render();
      }
    }
  }

  /**
   * Adds a child item to this renderable.
   * @param {Renderable} item 
   * @param {AddChildArgs} [options={}] 
   */
  addChild(item: Renderable, options: AddChildArgs = {}): void {
    const { index = -1, render = true, resize = true } = options;

    if (index === -1) {
      this._contentItems.push(item);
    } else {
      this._contentItems.splice(index, 0, item);
    }

    item.setContainer(this);

    if (resize) {
      this.resize();
    }

    if (render) {
      this._renderer.render();
    }
  }

  /**
   * Removes a content item from this renderable.
   * @param {Renderable} item 
   * @param {RemoveChildArgs} [options={}] 
   */
  removeChild(item: Renderable, options: RemoveChildArgs = {}): void {
    const { destroy = true, render = true } = options;
    const index = this._contentItems.indexOf(item);

    if (index === -1) {
      return;
    }

    if (destroy) {
      item.destroy();
    }

    this._contentItems.splice(index, 1);

    if (this._contentItems.length) {
      this.resize();
    } else {
      // If there are no more content items remove this item from the parent.
      this.remove();
    }

    if (render) {
      this._renderer.render();
    }
  }

  /**
   * Removes this item from it's parent. If there is no
   * parent then this renderable will just be destroyed.
   */
  remove(): void {
    if (this._container) {
      this._container.removeChild(this);
    } else {
      this.destroy();
    }
  }

  /**
   * Gets the index of a content renderable.
   * @param {Renderable} item 
   * @returns {number} 
   */
  getIndexOf(item: Renderable): number {
    return this._contentItems.indexOf(item);
  }

  /**
   * Gets a content renderable at an index.
   * @param {number} index 
   * @returns {(Renderable|null)} 
   */
  getAtIndex(index: number): Renderable|null {
    return this._contentItems[index] || null;
  }

  /**
   * Creates an Observable scoped to a specific event type. 
   * @template T The event type.
   * @param {Type<T>} Event 
   * @returns {Observable<T>} 
   */
  scope<T extends BusEvent<any>>(Event: Type<T>): Observable<T> {
    return this._eventBus.scope(Event);
  }

  /**
   * Whether a renderable is a descendant of this renderable.
   * @param {Renderable} item 
   * @returns {boolean} 
   */
  contains(item: Renderable): boolean {
    return this.getDescendants().indexOf(item) !== -1;
  }

  /**
   * Whether this renderable is a descendant of another renderable.
   * @param {Renderable} item 
   * @returns {boolean} 
   */
  isContainedWithin(item: Renderable): boolean {
    return item.contains(this);
  }

  /**
   * Gets the renderable area of this renderable.
   * @returns {RenderableArea} 
   */
  getArea(): RenderableArea {
    const { height, width, offsetX, offsetY } = this;

    return new RenderableArea(offsetX, offsetX + width, offsetY, offsetY + height);
  }

  /**
   * Creates a child renderable using this renderable as it's container.
   * @template T 
   * @param {RenderableArg<T>} renderable 
   * @param {ProviderArg[]} [providers=[]] 
   * @returns {T} 
   */
  createChild<T extends Renderable>(renderable: RenderableArg<T>, providers: ProviderArg[] = []): T {
    return RenderableInjector.fromRenderable(
      renderable,
      [
        { provide: ContainerRef, useValue: this },
        ...providers
      ],
      this.injector
    )
      .get(ConfiguredRenderable) as T;
  }

  /**
   * Handles any cleanup from a drop.
   */
  handleDropCleanup(): void {}

  /**
   * Whether this renderable can be dropped on.
   * @param {Renderable} target 
   * @returns {boolean} 
   */
  isDroppable(target: Renderable): boolean {
    return false;
  }
}

import { clamp } from '../utils';

/**
 * A class that defines an area in pixels.
 * @export
 * @class RenderableArea
 */
export class RenderableArea {
  private _width: number;
  private _height: number;
  private _surface: number;

  constructor(
    private _x: number = 0, 
    private _x2: number = 0, 
    private _y: number = 0, 
    private _y2: number = 0
  ) {
    this._calculate();
  }

  /**
   * The left x position.
   * @type {number}
   */
  get x(): number { return this._x; }
  set x(v: number) {
    this._x = v;
    this._calculate();
  }

  /**
   * The right x position.
   * @type {number}
   */
  get x2(): number { return this._x2; }
  set x2(v: number) {
    this._x2 = v;
    this._calculate();
  }

  /**
   * The top y position.
   * @type {number}
   */
  get y(): number { return this._y; }
  set y(v: number) {
    this._y = v;
    this._calculate();
  }

  /**
   * The bottom y position.
   * @type {number}
   */
  get y2(): number { return this._y2; }
  set y2(v: number) {
    this._y2 = v;
    this._calculate();
  }

  /**
   * The width of the area.
   * @readonly
   * @type {number}
   */
  get width(): number {
    return this._width;
  }

  /**
   * The height of the area.
   * @readonly
   * @type {number}
   */
  get height(): number {
    return this._height;
  }

  /**
   * The surface of the area.
   * @readonly
   * @type {number}
   */
  get surface(): number {
    return this._surface;
  }

  /**
   * Clamps a value to the x axis bounds. (x, x2)
   * @param {number} x 
   * @returns {number} 
   */
  clampX(x: number): number {
    return clamp(x, this.x, this.x2);
  }

  /**
   * Clamps a value to the y axis bounds. (y, y2)
   * @param {number} y
   * @returns {number} 
   */
  clampY(y: number): number {
    return clamp(y, this.y, this.y2);
  }

  private _calculate(): void {
    this._height = this._y2 - this._y;
    this._width = this._x2 - this._x;
    this._surface = this._height * this._width;
  }
}

import { ProviderArg, Injector, Type } from '../di';
import { ConfiguredRenderable } from './ConfiguredRenderable';
import { ConfigurationRef } from '../common';
import { Renderable } from './Renderable';
import { isFunction } from '../utils';

/**
 * Key used to assign the injector to on Renderables.
 * @see {@link RenderableInjector}
 * @type {string}
 */
export const INJECTOR_KEY = '__injector';

/**
 * An injector created for renderables.
 * @export
 * @class RenderableInjector
 * @extends {Injector}
 */
export class RenderableInjector extends Injector {
  /**
   * Creates an injector from a ConfiguredRenderable, a Renderable constructor or a Renderable instance.
   * The renderable is registered under the `ConfiguredRenderable` token and the configuration is under the
   * `ConfigurationRef` token.
   * @static
   * @param {(Type<Renderable>|ConfiguredRenderable<Renderable>|Renderable)} renderable 
   * @param {Provider[]} [providers=[]] 
   * @param {Injector} [parent] 
   * @param {{skipInit: ?boolean}} [options={}]
   * @returns {RenderableInjector} 
   */
  static fromRenderable(
    renderable: Type<Renderable>|ConfiguredRenderable<Renderable>|Renderable, 
    providers: ProviderArg[] = [],
    parent?: Injector,
    options: { skipInit?: boolean } = {}
  ): Injector {
    const { skipInit = false } = options;
    let Ctor = renderable;
    let config: any = null;
    let factory = (injector: Injector) => {
      const instance = injector.resolveAndInstantiate(Ctor) as Renderable;

      instance[INJECTOR_KEY] = injector;

      if (isFunction(instance.initialize) && skipInit !== true) {
        instance.initialize();
      }

      return instance;
    };

    if (renderable instanceof ConfiguredRenderable) {
      Ctor = renderable.renderable;
      config = renderable.config;
    } else if (renderable instanceof Renderable) {
      return new Injector([
        { provide: ConfiguredRenderable, useValue: renderable },
        { provide: ConfigurationRef, useValue: null },
        ...providers
      ]);
    }

    return Injector.fromInjectable(Ctor as Type<Renderable>, [
      { provide: ConfiguredRenderable, useFactory: factory, deps: [ Injector ] },
      { provide: ConfigurationRef, useValue: config },
      ...providers
    ], parent);
  }
}

import { VNode } from 'snabbdom/vnode';

import { Inject } from '../di';
import { Subject, Observable } from '../events';
import { DocumentRef, PatchRef, Patch } from '../common';

/**
 * Renders the layout.
 * @export
 * @class Renderer
 */
export class Renderer {
  /**
   * Notifies when a render cycle has finished.
   * @type {Observable<void>}
   */
  rendered: Observable<void>;

  private _rendered: Subject<void> = new Subject<void>();
  private _lastVNode: VNode|null = null;
  private _containerEl: Node;
  private _nodeGenerator: () => VNode;
  private _mountPoint: HTMLElement;

  /**
   * Creates an instance of Renderer.
   * @param {Document} _document 
   * @param {Patch} _patch 
   */
  constructor(
    @Inject(DocumentRef) private _document: Document,
    @Inject(PatchRef) private _patch: Patch
  ) {
    this.rendered = this._rendered.asObservable();
  }

  /**
   * Initializes the renderer with the containing DOM element to mount to.
   * @param {Node} containerEl 
   */
  initialize(containerEl: Node): void {
    this._mountPoint = this._document.createElement('div');
    this._containerEl = containerEl;
    this._containerEl.appendChild(this._mountPoint);
  }

  /**
   * Sets a the function that generates the virtual DOM tree.
   * @param {function(): VNode} fn 
   */
  useNodeGenerator(fn: () => VNode): void {
    this._nodeGenerator = fn;
  }

  /**
   * Updates the DOM with the current state of the renderable tree.
   * @returns {void} 
   */
  render(): void {
    if (!this._nodeGenerator) {
      return;
    }

    if (this._lastVNode) {
      this._lastVNode = this._patch(this._lastVNode, this._nodeGenerator());
    } else {
      this._lastVNode = this._patch(this._mountPoint, this._nodeGenerator());
    }

    this._rendered.next();
  }

  /**
   * Destroys the renderer.
   */
  destroy(): void {
    this.detach();
    this._rendered.complete();
  }

  /**
   * Detatches the renderer and it's DOM node from the containing element.
   */
  detach(): void {
    if (this._lastVNode) {
      if (this._lastVNode.elm) {
        this._containerEl.removeChild(this._lastVNode.elm);
      }
    } else {
      this._containerEl.removeChild(this._mountPoint);
    }
  }
}

import { get } from '../utils';

/**
 * A decorator that memoizes a functions computation based on the current
 * and previous value of other parameters.
 * @export
 * @param {...string[]} properties 
 * @returns {MethodDecorator} 
 */
export function MemoizeFrom(...properties: string[]): MethodDecorator {
  const propMap = new WeakMap();

  return (target: Object, name: string, descriptor: PropertyDescriptor): PropertyDescriptor => {
    const { value } = descriptor;
    let lastReturnValue;

    descriptor.value = function(...args: any[]): any {
      if (!propMap.has(this)) {
        propMap.set(this, {});
      }

      let props = propMap.get(this);
      let hasChanged = false;

      for (const prop of properties) {
        const result = get(this, prop);

        if (result !== props[prop]) {
          hasChanged = true;
        } 

        props[prop] = result;

        if (hasChanged) {
          break;
        }
      }            

      if (hasChanged) {
        lastReturnValue = value.apply(this, args);
      }

      return lastReturnValue;
    };

    return descriptor;
  };
}

