 *     e.wait(async () => {
 *       const confirmed = await this.confirmClose();
 * 
 *       if (!confirmed) {
 *         e.cancel(); 
 *       }
 *     });
 *   }
 * }
 */
export class BeforeDestroyEvent<T> extends Cancellable<T> {}

