/**
 * A layout is a set of renderables scoped to a drag host.
 * @export
 * @class Layout
 * @extends {Renderable}
 */
@Injectable({
  providers: [ DragHost ]
})
export class Layout extends Renderable {
  constructor(
    @Inject(ConfigurationRef) private _config: LayoutConfig|null,
    @Inject(ContainerRef) protected _container: Renderable,
    @Inject(DragHost) protected _dragHost: DragHost
  ) {
    super();
  }  

  /**
   * The height of the layout in pixels.
   * @readonly
   * @type {number}
   */
  get height(): number {
    return this._container.height;
  }

  /**
   * The width of the layout in pixels.
   * @readonly
   * @type {number}
   */
  get width(): number {
    return this._container.width;
  }

  initialize(): void {
    super.initialize();

    if (!this._config || !this._config.child) {
      throw new Error('A layout requires a child renderable.');
    }

    this._contentItems.push(this.createChild(this._config.child));
    this._dragHost.start.subscribe(this._onDragHostStart.bind(this));
  }

  /**
   * Creates this renderables virtual node.
   * @returns {VNode} 
   */
  render(): VNode {
    return h('div.ug-layout__layout', {
      style: {
        height: `${this.height}px`,
        width: `${this.width}px`
      }
    }, 
      this._contentItems.map(i => i.render())
    );
  }

  /**
   * Destroys this renderable.
   */
  destroy(): void {
    this._dragHost.destroy();
    super.destroy();
  }

  /**
   * Gets the visible areas of all descendant Renderables.
   * @returns {Array<{ item: Renderable, area: RenderableArea }>} 
   */
  getItemVisibleAreas(): Array<{ item: Renderable, area: RenderableArea }> {
    return this.getDescendants()
      .filter(item => item.isVisible())
      .map(item => {
        return {
          item,
          area: item.getArea()
        };
      });
  }

  private _onDragHostStart(container: DragHostContainer): void {
    const { offsetX, offsetY, height, width } = this;
    const { dragArea, item } = container;

    this._dragHost.bounds = new RenderableArea(offsetX, width + offsetX - dragArea.width, offsetY, height + offsetY - dragArea.height);
    this._dragHost.setDropAreas(this._getDropTargets(item));
  }

  private _getDropTargets(target: Renderable): DropArea[] {
    return this.getItemVisibleAreas()
      .filter(({ item }) => {
        return DragHost.isDropTarget(item) 
          && item !== target
          && item.isDroppable(target)
          && !target.contains(item)
          && !target.isContainedWithin(item);
      }) as any; 
  }

  /**
   * Configures a layout renderable.
   * @static
   * @param {LayoutConfig} config 
   * @returns {ConfiguredRenderable<Layout>} 
   */
  static configure(config: LayoutConfig): ConfiguredRenderable<Layout> {
    return new ConfiguredRenderable(Layout, config);    
  }
}

import { ConfiguredRenderable } from '../dom';
import { Inject } from '../di';
import { SerializerContainer, Serializer, Serialized } from '../serialization';
import { Layout } from './Layout';

export interface SerializedLayout extends Serialized {
  child: Serialized;
}

/**
 * Serializes/deserializes a layout renderable.
 * @export
 * @class LayoutSerializer
 * @implements {Serializer<Layout, SerializedLayout>}
 */
export class LayoutSerializer implements Serializer<Layout, SerializedLayout> {
  /**
   * Creates an instance of LayoutSerializer.
   * @param {SerializerContainer} _container 
   */
  constructor(
    @Inject(SerializerContainer) private _container: SerializerContainer
  ) {}

  /**
   * Serializes a Layout renderable.
   * @param {Layout} node 
   * @returns {SerializedLayout} 
   */
  serialize(node: Layout): SerializedLayout {
    return {
      name: 'Layout',
      child: this._container.serialize(node.getChildren()[0])
    };
  }

  /**
   * Deserializes a serialized layout node.
   * @param {SerializedLayout} node 
   * @returns {ConfiguredRenderable<Layout>} 
   */
  deserialize(node: SerializedLayout): ConfiguredRenderable<Layout> {
    return Layout.configure({
      child: this._container.deserialize(node.child)
    });
  }

  /**
   * Invoked when the serializer is registered.
   * @static
   * @param {SerializerContainer} container 
   */
  static register(container: SerializerContainer): void {
    container.registerClass('Layout', Layout);
  }
}

import { Serializer, Serialized } from './common';
import { SerializerContainer } from './SerializerContainer';
import { Renderable } from '../dom';
import { RenderableArg, ConfigureableType } from '../common';

/**
 * A serializer that can be used for renderables that don't container
 * any special logic or configuration.
 * @export
 * @class GenericSerializer
 * @implements {Serializer<R, Serialized>}
 * @template R The renderable type.
 */
export class GenericSerializer<R extends Renderable> implements Serializer<R, Serialized> {
  /**
   * Creates an instance of GenericSerializer.
   * @param {string} _name 
   * @param {ConfigureableType<R>} _Class 
   */
  constructor(
    protected _name: string,
    protected _Class: ConfigureableType<R>
  ) {}

  /**
   * Serializes the renderable.
   * @param {R} node 
   * @returns {Serialized} 
   */
  serialize(node: R): Serialized {
    return { name: this._name } ;
  }

  /**
   * Deserializes the node.
   * @param {Serialized} node 
   * @returns {RenderableArg<R>} 
   */
  deserialize(node: Serialized): RenderableArg<R> {
    return this._Class;
  }

  /**
   * Registers this class with the container.
   * @param {SerializerContainer} container 
   */
  register(container: SerializerContainer): void {
    container.registerClass(this._name, this._Class);
  }
}

import { Injector, Type } from '../di';
import { Renderable } from '../dom';
import { Serializer, Serialized } from './common';
import { isFunction, isString } from '../utils';
import { RenderableArg } from '../common';

export interface Constructable<T> {
  constructor: Function;
}

export type BaseSerializer = Serializer<Renderable, Serialized>;

export interface SerializerContainerConfig {
  /**
   * An injector to use as the parent injector.
   * @type {Injector}
   */
  injector?: Injector;
}

/**
 * A container for running and determining serializers.
 * @export
 * @class SerializerContainer
 * @example
 * 
 * const container = new SerializerContainer();
 * 
 * class MyClass {}
 * 
 * const mySerializer = new GenericSerializer('MyClass', MyClass);
 * 
 * container.registerSerializer(MyClass, mySerializer);
 * container.serialize(new MyClass()); // => { name: 'MyClass' }
 * container.deserialize({ name: 'MyClass' }); // => MyClass
 */
export class SerializerContainer {
  protected _injector;
  protected _serializers: Map<Type<any>, BaseSerializer|Type<BaseSerializer>> = new Map();
  protected _classToStringMap: Map<Type<any>, string> = new Map();

  /**
   * Creates an instance of SerializerContainer.
   * @param {SerializerContainerConfig} [config={}] 
   */
  constructor(config: SerializerContainerConfig = {}) {
    this._injector = new Injector([], config.injector || null);
    this._injector.registerProvider({ provide: SerializerContainer, useValue: this });
  }

  /**
   * Registers a serializer with the container.
   * @param {Type<any>} _Class 
   * @param {(BaseSerializer|Type<BaseSerializer>)} serializer 
   * @param {{ skipRegister?: boolean }} [options={}] 
   */
  registerSerializer(_Class: Type<any>, serializer: BaseSerializer|Type<BaseSerializer>, options: { skipRegister?: boolean } = {}): void {
    const { skipRegister = false } = options;

    this._serializers.set(_Class, serializer);
    this._injector.registerProvider(isFunction(serializer) ? serializer : { provide: serializer, useValue: serializer });

    if (!skipRegister && isFunction(serializer['register'])) {
      serializer['register'](this);
    }
  }

  /**
   * Registers a class that can be identified by a string representation.
   * @param {string} name 
   * @param {Type<any>} _Class 
   */
  registerClass(name: string, _Class: Type<any>): void {
    this._classToStringMap.set(_Class, name);
    this._injector.registerProvider({ provide: name, useValue: _Class });
  }

  /**
   * Registers multiple classes that can be identified by a string representation.
   * @param {{ [key:string]: Type<any> }} [classes={}] 
   */
  registerClasses(classes: { [key:string]: Type<any> } = {}): void {
    for (const key of Object.keys(classes)) {
      this.registerClass(key, classes[key]);
    }
  }

  /**
   * Resolves a class to it's registered string representation.
   * @param {Type<any>} _Class 
   * @returns {(string|null)} 
   */
  resolveClassString(_Class: Type<any>): string|null {
    return this._classToStringMap.get(_Class) || null;
  }

  /**
   * Resolves a serializer from a serialized node.
   * @param {Serialized} node 
   * @returns {(BaseSerializer|null)} 
   */
  resolveFromSerialized(node: Serialized): BaseSerializer|null {
    const _Class = this.resolveClass(node.name);

    if (_Class) {
      if (this._serializers.has(_Class)) {
        return this._injector.get(this._serializers.get(_Class));
      }
    }

    return null;
  }

  /**
   * Resolves a serializer from a class.
   * @param {Type<any>} _Class 
   * @returns {(BaseSerializer|null)} 
   */
  resolveFromClass(_Class: Type<any>): BaseSerializer|null {
    if (this._serializers.has(_Class)) {
      return this.resolve<BaseSerializer>(this._serializers.get(_Class));
    }

    return null;
  }

  /**
   * Resolves a serializer from an instance of a registered class.
   * @param {(Renderable & Constructable<any>)} instance 
   * @returns {(BaseSerializer|null)} 
   */
  resolveFromInstance(instance: Renderable & Constructable<any>): BaseSerializer|null {
    return this.resolveFromClass(instance.constructor as Type<any>);
  }

  /**
   * Resolves a string to a registered class.
   * @param {string} name 
   * @returns {(Type<any>|null)} 
   */
  resolveClass(name: string): Type<any>|null {
    return this.resolve<Type<any>>(name);
  }

  /**
   * Serializes a class instance using the registered serializer for that class.
   * @template R The class type.
   * @template S The serialized type.
   * @param {R} instance 
   * @returns {S} The serialized node.
   */
  serialize<R extends Renderable & Constructable<any>, S extends Serialized>(instance: R): S {
    const serializer = this.resolveFromInstance(instance) as Serializer<R, S>|null;

    if (!serializer) {
      throw new Error(`Serializer for class '${instance.constructor.name}' is not registered.`);
    }

    return serializer.serialize(instance);
  }

  /**
   * Deserializes a serialized node using the registered serializer for that node type.
   * @template R The class type.
   * @template S The serialized type.
   * @param {S} serialized 
   * @returns {R} The renderable argument.
   */
  deserialize<R extends Renderable, S extends Serialized>(serialized: S): RenderableArg<R> {
    const serializer = this.resolveFromSerialized(serialized) as Serializer<R, S>|null;

    if (!serializer) {
      throw new Error(`Serializer for node '${serialized.name}' is not registered.`);
    }

    return serializer.deserialize(serialized);
  }

  /**
   * Resolves a token with this containers injector.
   * @template T The return type.
   * @param {*} token 
   * @returns {(T|null)} 
   */
  resolve<T>(token: any): T|null {
    return this._injector.get(token, null);
  }
}

