import { StackItemContainer } from './StackItemContainer';

export interface StackHeaderConfig {
  size: number;
  distribute: boolean;
  droppable: boolean;
}

export type StackHeaderConfigArgs = {
  [P in keyof StackHeaderConfig]?: StackHeaderConfig[P];
}

export const DEFAULT_STACK_HEADER_SIZE = 25;

export class StackHeader extends Renderable implements DropTarget {
  protected _contentItems: StackTab[] = [];
  private _controls: StackControl[] = [];
  private _config: StackHeaderConfig;
  private _tabAreas: RenderableArea[];

  constructor(
    @Inject(ConfigurationRef) _config: StackHeaderConfigArgs|null,
    @Inject(ContainerRef) protected _container: Stack,
    @Inject(DragHost) protected _dragHost: DragHost
  ) {
    super();

    this._config = Object.assign({
      controls: [],
      size: DEFAULT_STACK_HEADER_SIZE,
      droppable: true,
      distribute: false
    }, _config);
  } 

  get width(): number {
    return this._container.isHorizontal ? this._container.width : this._config.size;
  }

  get height(): number {
    return this._container.isHorizontal ? this._config.size : this._container.height;
  }

  get size(): number {
    return get(this._config, 'size', DEFAULT_STACK_HEADER_SIZE);
  }

  get droppable(): boolean {
    return get(this._config, 'droppable', true);
  }

  get isHorizontal(): boolean {
    return this._container.isHorizontal;
  }

  get isDistributed(): boolean {
    return get(this._config, 'distribute', false);
  }

  get controls(): StackControl[] {
    return this._controls.slice(0);
  }

  initialize(): void {
    super.initialize();

    this._dragHost.start
      .takeUntil(this.destroyed)
      .subscribe(this._onDragHostStart.bind(this));

    this._dragHost.dropped
      .takeUntil(this.destroyed)
      .subscribe(this._onDragHostDropped.bind(this));
  }

  addTab(config: StackTabConfigArgs, options: AddChildArgs = {}): StackTab {
    const tab = this.createChild(new ConfiguredRenderable(StackTab, config), [ Draggable ]);

    tab.subscribe(TabSelectionEvent, e => this.emit(e));
    tab.subscribe(TabCloseEvent, e => this.emit(e));
    tab.subscribe(TabDragEvent, e => this.emit(e));

    this.addChild(tab, options);

    return tab;
  }

  addControl(_control: RenderableArg<StackControl>): void {
    this._controls.push(this.createChild(_control));
  }

  isTabActive(tab: StackTab): boolean {
    return this._container.isActiveTab(tab);
  }

  getItemFromTab(tab: StackTab): StackItemContainer|null {
    return this._container.getAtIndex(this.getIndexOf(tab)) as StackItemContainer|null;
  }

  render(): VNode {
    const [ postTabControls, preTabControls ] = partition(
      this._controls.filter(c => c.isActive()),
      propEq('position', StackControlPosition.POST_TAB)
    );

    return h('div.ug-layout__stack-header', {
      style: {
        height: `${this.height}px`,
        width: `${this.width}px`
      }
    }, 
      [
        h('div.ug-layout__stack-controls', preTabControls.map(c => c.render())),
        h('div.ug-layout__tab-container', this._contentItems.map(tab => tab.render())),
        h('div.ug-layout__stack-controls', postTabControls.map(c => c.render()))
      ]
    );
  }

  isDroppable(): boolean {
    return this.droppable;
  }

  getChildren(): Renderable[] {
    return [
      ...this._controls,
      ...this._contentItems
    ];
  }

  handleDrop(item: Renderable, dropArea: DropArea, e: DragEvent<Renderable>): void {
    const index = this._getIndexFromArea(e.pageX, e.pageY, dropArea.area) + 1;

    if (item instanceof StackItemContainer && this._container.getIndexOf(item) === -1) {
      this._container.addChild(item, { index });
      this._container.setActiveIndex(index);
    } 

    this.onDropHighlightExit();
  }

  getHighlightCoordinates(args: HighlightCoordinateArgs): RenderableArea {
    let { pageX, pageY, dragArea, dropArea: { item, area: { x, x2, y, y2, height } } } = args;

    const highlightArea = new RenderableArea(x, dragArea.width + x, y, y2);
    let leftMostTabIndex = this._getIndexFromArea(pageX, pageY, args.dropArea.area);
    let leftMostTabArea = this._tabAreas[leftMostTabIndex];

    if (leftMostTabArea) {
      highlightArea.x = leftMostTabArea.x2;
      highlightArea.x2 = leftMostTabArea.x2 + dragArea.width;
    }

    for (const [ index, tab ] of this._contentItems.entries()) {
      if (!tab.isDragging) {
        tab.element.style.transform = index > leftMostTabIndex ? `translateX(${dragArea.width}px)` : 'translateX(0px)';
      }
    }

    return highlightArea;
  }

  onDropHighlightExit(): void {
    for (const tab of this._contentItems) {
      if (!tab.isDragging) {
        tab.element.style.transform = 'translateX(0px)';
      }
    }
  }

  getOffsetXForTab(tab: StackTab): number {
    if (this.isHorizontal) {
      return this._contentItems.slice(0, this.getIndexOf(tab)).reduce((result, tab) => result + tab.width, this.offsetX);
    }

    return this.offsetX;
  }

  getOffsetYForTab(tab: StackTab): number {
    if (!this.isHorizontal) {
      return this._contentItems.slice(0, this.getIndexOf(tab)).reduce((result, tab) => result + tab.height, this.offsetY);
    }

    return this.offsetY;
  }

  private _onDragHostStart(): void {
    this._tabAreas = this._contentItems.map(tab => tab.getArea());
  }

  private _onDragHostDropped(): void {
    this._tabAreas = [];
  }

  private _getIndexFromArea(pageX: number, pageY: number, area: RenderableArea): number {
    let { x, y } = area;

    const deltaX = pageX - x;
    const deltaY = pageY - y;

    let result = -1;

    for (const [ index, tabArea ] of this._tabAreas.entries()) {
      if (deltaX >= (tabArea.x - x) + (tabArea.width / 2)) {
        result = index;
      }
    }

    return result;
  }
}

import { Cancellable } from '../events';
import { StackItemContainer } from './StackItemContainer';

export class StackItemCloseEvent extends Cancellable<StackItemContainer> {}

import { VNode } from 'snabbdom/vnode';
import h from 'snabbdom/h';

import { Inject, Injector } from '../di'
import { Renderer, Renderable, AddChildArgs, RenderableInjector, ConfiguredRenderable, RenderableArea } from '../dom';
import { BeforeDestroyEvent, Cancellable, Subject, Observable } from '../events';
import { MakeVisibleCommand } from '../commands';
import {
  ConfigurationRef,
  ContainerRef,
  RenderableArg,
  DropTarget,
  DropArea,
  DragEvent,
  HighlightCoordinateArgs
} from '../common';
import { Stack } from './Stack';
import { StackTab } from './StackTab';
import { StackItemCloseEvent } from './StackItemCloseEvent';
import { XYContainer } from '../XYContainer';
import { StackRegion } from './common';
import { get } from '../utils'
import { TabControl, CloseTabControl } from './tabControls';
import { StackControlConfig } from './controls';

export interface StackItemContainerConfig {
  use: RenderableArg<Renderable>;
  title?: string;
  droppable?: boolean;
  draggable?: boolean;
  closeable?: boolean;
  tabControls?: RenderableArg<TabControl>[];
}

/**
 * A renderable that is a direct child of a stack. Handles a renderable item.
 * @export
 * @class StackItemContainer
 * @extends {Renderable}
 * @implements {DropTarget}
 */
export class StackItemContainer extends Renderable implements DropTarget {
  private _controls: TabControl[] = [];

  /**
   * Creates an instance of StackItemContainer.
   * @param {StackItemContainerConfig} _config 
   * @param {Stack} _container 
   */
  constructor(
    @Inject(ConfigurationRef) private _config: StackItemContainerConfig,
    @Inject(ContainerRef) protected _container: Stack
  ) {
    super();
  }

  get container(): Stack {
    return this._container as Stack;
  }

  get controls(): TabControl[] {
    return [ ...this._controls ];
  }

  get tab(): StackTab|null {
    return this._container.getTabAtIndex(this._container.getIndexOf(this));
  }

  get width(): number {
    return this.container.isHorizontal
      ? this.container.width
      : Math.max(this.container.width - this.container.header.width, 0);
  }

  get height(): number {
    return this.container.isHorizontal
      ? Math.max(this.container.height - this.container.header.height, 0)
      : this.container.height;
  }

  get isActive(): boolean {
    return this.container.isActiveContainer(this);
  }

  get offsetY(): number {
    if (this.container.isHorizontal) {
      if (!this.container.isReversed) {
        return this.container.offsetY + this.container.header.height
      }
    }

    return this.container.offsetY;
  }

  get draggable(): boolean {
    return Boolean(get(this._config, 'draggable', true));
  }

  get droppable(): boolean {
    return Boolean(get(this._config, 'droppable', true));
  }

  get closeable(): boolean {
    return Boolean(get(this._config, 'closeable', false));
  }

  get title(): string {
    return (this._config && this._config.title) || '';
  }

  get offsetX(): number {
    if (!this.container.isHorizontal) {
      if (!this.container.isReversed) {
        return this.container.offsetX + this.container.header.width
      }
    }

    return this.container.offsetX;
  }

  protected get _item(): Renderable {
    return this._contentItems[0];
  }

  initialize(): void {
    super.initialize();

    this._contentItems = [
      this.createChild(this._config.use)
    ];

    this._config.tabControls = this._config.tabControls || [];

    if (!ConfiguredRenderable.inList(this._config.tabControls, CloseTabControl)) {
      this._config.tabControls.push(CloseTabControl);
    }

    for (const control of this._config.tabControls) {
      this.addControl(control, { resize: false, render: false });
    }

    this.subscribe(MakeVisibleCommand, this.makeVisible.bind(this));
  }

  render(): VNode {
    return h('div.ug-layout__stack-item-container', {
      key: this._uid,
      props: {
        hidden: !this.isActive
      },
      style: {
        height: `${this.height}px`,
        width: `${this.width}px`
      }
    }, [ this._item.render() ]);
  }

  isVisible(): boolean {
    return this.container.isVisible() && this._container.isActiveContainer(this);
  }

  makeVisible(): void {
    this._container.setActiveContainer(this);
  }

  getChildren(): Renderable[] {
    return [ this._item ];
  }

  handleDrop(item: StackItemContainer, dropArea: DropArea, e: DragEvent<Renderable>): void {
    const region = this._getRegionFromArea(e.pageX, e.pageY, dropArea.area);

    this._container.handleItemDrop(region as StackRegion, item);
    this._renderer.render();
  }

  getHighlightCoordinates(args: HighlightCoordinateArgs): RenderableArea {
    const { pageX, pageY, dropArea: { area: { x, x2, y, y2 } } } = args
    const highlightArea = new RenderableArea(x, x2, y, y2);
    const region = this._getRegionFromArea(pageX, pageY, args.dropArea.area);

    switch (region) {
      case StackRegion.WEST:
        highlightArea.x2 = (this.width / 2) + x;
        break;
      case StackRegion.EAST:
        highlightArea.x = (this.width / 2) + x;
        break;
      case StackRegion.NORTH:
        highlightArea.y2 = (this.height / 2) + y;
        break;
      case StackRegion.SOUTH:
        highlightArea.y = (this.height / 2) + y;
        break;
    }

    return highlightArea;
  }

  isDroppable(): boolean {
    return this.droppable;
  }

  onDropHighlightExit(): void {}

  addControl(control: RenderableArg<TabControl>, options: AddChildArgs = {}): void {
    const { index = -1, render = true, resize = true } = options;
    const { tab } = this;

    const newControl = this.createChild(control);

    if (index !== -1) {
      this._controls.splice(index, 0, newControl);
    } else {
      this._controls.push(newControl);
    }

    if (tab && resize) {
      tab.resize();
    }

    if (render) {
      this._renderer.render();
    }
  }

  setContainer(container: Stack): void {
    if (container === this._container) {
      return;
    }

    super.setContainer(container);

    this._container.scope(StackItemCloseEvent)
      .filter(e => e.target === this)
      .takeUntil(this.containerChange)
      .subscribe(this._onTabClose.bind(this));
  }

  private _getRegionFromArea(pageX: number, pageY: number, area: RenderableArea): StackRegion|null {
    const { x, x2, y, y2 } = area;
    const deltaX = pageX - x;
    const deltaY = pageY - y;

    if (deltaX < this.width / 3) {
      return StackRegion.WEST;
    } else if (deltaX > (this.width / 3) * 2) {
      return StackRegion.EAST;
    } else if (deltaY < this.height / 2) {
      return StackRegion.NORTH;
    } else if (deltaY >= this.height / 2) {
      return StackRegion.SOUTH;
    }

    return null;
  }

  private _onTabClose(e: StackItemCloseEvent): void {
    this.emitDown(e.delegate(BeforeDestroyEvent, this));
  }
}

import { ConfiguredRenderable } from '../dom';
import { Inject } from '../di';
import { GenericSerializer, SerializerContainer, Serializer, Serialized } from '../serialization';
import { Stack } from './Stack';
import { StackTab } from './StackTab';
import { StackHeaderConfig } from './StackHeader';
import { StackControl, MinimizeStackControl, CloseStackControl, StackControlSerializer } from './controls'
import { TabControl, CloseTabControl } from './tabControls'
import { XYDirection } from '../common';

export interface SerializedStackItem {
  use: Serialized;
  title: string;
  droppable: boolean;
  draggable: boolean;
  closeable: boolean;
  tabControls: Serialized[];
}

export interface SerializedStack extends Serialized {
  children: SerializedStackItem[];
  startIndex: number;
  direction: XYDirection;
  reverse: boolean;
  header: StackHeaderConfig|null;
  controls: Serialized[];
}

export class StackSerializer implements Serializer<Stack, SerializedStack> {
  constructor(
    @Inject(SerializerContainer) private _container: SerializerContainer
  ) {}

  serialize(node: Stack): SerializedStack {
    return {
      name: 'Stack',
      startIndex: node.activeIndex,
      direction: node.direction,
      reverse: node.isReversed,
      header: !node.header ? null : {
        size: node.header.size,
        droppable: node.header.droppable,
        distribute: node.header.isDistributed
      },
      controls: !node.header ? [] : node.header.controls.map(control => {
        return this._container.serialize(control);
      }),
      children: node.items.map(item => {
        return {
          use: this._container.serialize(item.getChildren()[0]),
          title: item.title,
          draggable: item.draggable,
          droppable: item.droppable,
          closeable: item.closeable,
          tabControls: item.controls.map(control => {
            return this._container.serialize(control);
          })
        };
      })
    }  
  }

  deserialize(node: SerializedStack): ConfiguredRenderable<Stack> {
    return Stack.configure({
      startIndex: node.startIndex,
      direction: node.direction,
      reverse: node.reverse,
      header: !node.header ? {} : {
        size: node.header.size,
        droppable: node.header.droppable,
        distribute: node.header.distribute
      },
      controls: node.controls.map(control => {
        return this._container.deserialize<StackControl, Serialized>(control);
      }),
      children: node.children.map(child => {
        return {
          title: child.title,
          draggable: child.draggable,
          droppable: child.droppable,
          closeable: child.closeable,
          use: this._container.deserialize(child.use),
          tabControls: child.tabControls.map(tabCtrl => {
            return this._container.deserialize<TabControl, Serialized>(tabCtrl);
          })
        };
      })
    })
  }

  static register(container: SerializerContainer): void {
    container.registerClass('Stack', Stack);  
    container.registerSerializer(MinimizeStackControl, new StackControlSerializer('MinimizeStackControl', MinimizeStackControl));
    container.registerSerializer(CloseStackControl, new StackControlSerializer('CloseStackControl', CloseStackControl));
    container.registerSerializer(CloseTabControl, new GenericSerializer('CloseTabControl', CloseTabControl));
  }
}

import { VNode } from 'snabbdom/vnode';
import h from 'snabbdom/h';

import { Inject, Injector } from '../di'
import { 
  Subject, 
  Observable, 
  Cancellable,
  BeforeDestroyEvent
} from '../events';
import { TabSelectionEvent } from './TabSelectionEvent';
import { Renderable, ConfiguredRenderable, MemoizeFrom } from '../dom';
import { Draggable } from '../Draggable';
import { ContainerRef, ConfigurationRef, DocumentRef, DragEvent, DragStatus } from '../common';
import { StackHeader } from './StackHeader';
import { TabCloseEvent } from './TabCloseEvent';
import { TabDragEvent } from './TabDragEvent';
import { Stack } from './Stack';
import { StackItemContainer } from './StackItemContainer';
import { DragHost } from '../DragHost';
import { get } from '../utils';
import { TabControl } from './tabControls';

export interface StackTabConfig {
  maxSize: number;
  title: string;
}

export type StackTabConfigArgs = {
  [P in keyof StackTabConfig]?: StackTabConfig[P];
}

/**
 * Renderable representing a stack tab.
 * @export
 * @class StackTab
 * @extends {Renderable}
 */
export class StackTab extends Renderable {
  private _element: HTMLElement;
  private _isDragging: boolean = false;
  protected _container: StackHeader;

  /**
   * Creates an instance of StackTab.
   * @param {StackTabConfig} _config 
   * @param {Draggable<StackTab>} _draggable 
   * @param {DragHost} _dragHost 
   * @param {Document} _document 
   */
  constructor(
    @Inject(ConfigurationRef) private _config: StackTabConfig,
    @Inject(Draggable) private _draggable: Draggable<StackTab>,
    @Inject(DragHost) private _dragHost: DragHost,
    @Inject(DocumentRef) private _document: Document
  ) {
    super();

    this._config = Object.assign({
      maxSize: 150,
      title: ''   
    }, this._config || {});
  }

  get width(): number {
    return this._container.isHorizontal ? this._width : this._container.width;
  }

  get height(): number {
    return this._container.isHorizontal ? this._container.height : this._height;
  }

  /**
   * The HTML element for this tab.
   * @readonly
   * @type {HTMLElement}
   */
  get element(): HTMLElement {
    return this._element;
  }

  /**
   * The StackItemContainer associated with this tab.
   * @see {@link StackItemContainer}
   * @readonly
   * @type {(StackItemContainer|null)}
   */
  get item(): StackItemContainer|null {
    return this._container.getItemFromTab(this);
  }

  get offsetX(): number {
    return this._container.getOffsetXForTab(this);
  }

  get offsetY(): number {
    return this._container.getOffsetYForTab(this);
  }

  /**
   * Whether this tab is dragging.
   * @readonly
   * @type {boolean}
   */
  get isDragging(): boolean {
    return this._isDragging;
  }

  /**
   * The stack that this tab belongs to.
   * @readonly
   * @type {(Stack|null)}
   */
  get stack(): Stack|null {
    return (this._container ? this._container.container : null) as Stack|null;
  }

  /**
   * The list of tab controls.
   * @readonly
   * @type {TabControl[]}
   */
  get controls(): TabControl[] {
    const { item } = this;

    return item ? item.controls : [];
  }

  private get _resizeHashId(): string {
    return [
      get(this.item, 'title'),
      this._element ? 1 : 0,
      this.controls.map(c => c.isActive() ? c.uid : '').join('')
    ].join(':');
  }

  initialize(): void {
    super.initialize();

    this._draggable.drag
      .filter(Draggable.isDraggingEvent)
      .subscribe(this._onDragMove.bind(this));

    this._draggable.drag
      .filter(Draggable.isDragStopEvent)
      .subscribe(this._onDragStop.bind(this));

    this._draggable.drag
      .filter(Draggable.isDragStartEvent)
      .subscribe(this._onDragStart.bind(this));

    this._dragHost.start
      .takeUntil(this.destroyed)
      .subscribe(this._onDragHostStart.bind(this));

    this._dragHost.dropped
      .takeUntil(this.destroyed)
      .subscribe(this._onDragHostDropped.bind(this));
  }

  @MemoizeFrom('_resizeHashId')
  resize(): void {
    if (this._element) {
      const rect = this._element.getBoundingClientRect();

      this._width = rect.width;
      this._height = rect.height;
    }
  }

  render(): VNode {
    const { item } = this;

    return h(`div.ug-layout__stack-tab`, {
      key: this.uid,
      style: this._getStyles(),
      class: {
        'ug-layout__stack-tab-active': this._container.isTabActive(this),
        'ug-layout__stack-tab-distributed': this._container.isDistributed,
        'ug-layout__stack-tab-x': this._container.isHorizontal,
        'ug-layout__stack-tab-y': !this._container.isHorizontal,
        'ug-layout__stack-tab-draggable': item ? item.draggable : true
      },
      hook: {
        create: (oldNode, newNode) => this._element = newNode.elm as HTMLElement,
        insert: () => this.resize()
      },
      on: {
        mousedown: e => this._onMouseDown(e),
        click: () => this._onClick()
      }
    }, [
      h('div', get(item, 'title', '')),
      h('div.ug-layout__stack-tab-controls', this.controls.filter(c => c.isActive()).map(c => c.render()))
    ]);
  }  

  destroy(): void {
    this._draggable.destroy();
    super.destroy();
  }

  private _getStyles(): { [key: string]: string } {
    let result = {};

    if (this._container.isHorizontal) {
      if (!this._container.isDistributed) {
        result['max-width'] = `${this._config.maxSize}px`;
      }

      result['max-height'] = `${this._container.height}px`;
      result['height'] = `${this.height}px`;
    } else {
      if (!this._container.isDistributed) {
        result['max-height'] = `${this._config.maxSize}px`;
      }

      result['max-width'] = `${this._container.width}px`;
      result['width'] = `${this.width}px`;
    }

    return result;
  }

  private _onMouseDown(e: MouseEvent): void {
    const { item } = this;

    if (item && !item.draggable) {
      return;  
    }

    this._draggable.startDrag({
      host: this, 
      startX: e.pageX,
      startY: e.pageY
    });
  }

  private _onDragStart(e: DragEvent<StackTab>): void {
    const item = this._container.getItemFromTab(this);
    const originStack = this.stack;
    const originIndex = this._container.getIndexOf(this);

    this._isDragging = true;
    this.emit(new TabDragEvent(this));
    this._element.classList.add('ug-layout__tab-dragging');
    this._document.body.appendChild(this._element);

    if (item) {
      this._dragHost.initialize({
        item: <Renderable>item, 
        draggable: this._draggable, 
        dragArea: this.getArea()
      });

      this._dragHost.fail
        .takeUntil(this._dragHost.dropped)
        .subscribe(() => {
          if (originStack) {
            originStack.addChild(item, { index: originIndex });
          }
        });

      this._dragHost.dropped
        .first()
        .subscribe(() => this.destroy());
    } 
  }

  private _onDragMove(e: DragEvent<StackTab>): void {
    const { bounds } = this._dragHost;

    let x = e.pageX - (this.width / 2);
    let y = e.pageY - (this.height / 2);

    if (bounds) {
      x = bounds.clampX(x);
      y = bounds.clampY(y);
    }

    this._element.style.transform = `translateX(${x}px) translateY(${y}px)`;
  }

  private _onDragStop(e: DragEvent<StackTab>): void {
    this._isDragging = false;
    this._element.style.transform = `translateX(0px) translateY(0px)`;
    this._element.classList.remove('ug-layout__tab-dragging');
    this._document.body.removeChild(this._element);
  }

  private _onDragHostStart(): void {
    this._element.classList.add('ug-layout__tab-drag-enabled');
  }

  private _onDragHostDropped(): void {
    this._element.classList.remove('ug-layout__tab-drag-enabled');
  }

  private _onClick(): void {
    this.emit(new TabSelectionEvent(this));
  }
}

