import { Inject } from './di';
import { 
  DocumentRef, 
  DropTarget, 
  DropArea, 
  RenderableDropTarget,
  DragStatus,
  DragEvent
} from './common';
import { Renderable, RenderableArea } from './dom';
import { Observable, Subject } from './events';
import { Draggable } from './Draggable';
import { isObject, isFunction, clamp } from './utils';

export class DragHostContainer {
  item: Renderable;
  dragArea: RenderableArea;
  draggable: Draggable<Renderable>;
}

export class DragHost {
  dropped: Observable<DropArea>;
  start: Observable<DragHostContainer>;
  fail: Observable<Renderable>;
  success: Observable<Renderable>;

  private _item: Renderable;
  private _dropped: Subject<DropArea> = new Subject();
  private _start: Subject<DragHostContainer> = new Subject();
  private _fail: Subject<Renderable> = new Subject();
  private _success: Subject<Renderable> = new Subject();
  private _areas: DropArea[]|null = null;
  private _dropArea: DropArea|null = null;
  private _element: HTMLElement = this._document.createElement('div');
  private _bounds: RenderableArea|null = null;
  private _dragArea: RenderableArea;

  get bounds(): RenderableArea|null {
    return this._bounds;
  }

  set bounds(val: RenderableArea|null) {
    this._bounds = val;
  }

  constructor(
    @Inject(DocumentRef) private _document: Document
  ) {
    this.dropped = this._dropped.asObservable();
    this.start = this._start.asObservable();
    this.fail = this._fail.asObservable();
    this.success = this._success.asObservable();

    this._element.classList.add('ug-layout__drop-indicator');
    this._element.hidden = true;
    this._document.body.appendChild(this._element);
  }

  destroy(): void {
    if (this._document.body.contains(this._element)) {
      this._document.body.removeChild(this._element);
    }
  }

  initialize(container: DragHostContainer): void {
    const { item, dragArea, draggable } = container;

    this._item = item;
    this._dragArea = dragArea;
    this._start.next(container);

    draggable.drag
      .filter(Draggable.isDraggingEvent)
      .takeUntil(draggable.drag.filter(Draggable.isDragStopEvent))
      .subscribe(this._onDrag.bind(this));

    draggable.drag
      .filter(Draggable.isDragStopEvent)
      .takeUntil(this._dropped)
      .subscribe(this._onDragStop.bind(this));

    this._element.hidden = false;
  } 

  setDropAreas(areas: DropArea[]): void {
    this._areas = areas;
  }

  private _onDrag(e: DragEvent<Renderable>): void {
    if (!this._areas) {
      return;
    }

    const pageX = this._bounds ? this._bounds.clampX(e.pageX) : e.pageX;
    const pageY = this._bounds ? this._bounds.clampY(e.pageY) : e.pageY;

    let minSurface = Infinity;
    let result: DropArea|null = null;

    for (const entry of this._areas) {
      if (
        pageX >= entry.area.x &&
        pageX <= entry.area.x2 &&
        pageY >= entry.area.y &&
        pageY <= entry.area.y2 &&
        minSurface > entry.area.surface
      ) {
        minSurface = entry.area.surface;
        result = entry;  
      }
    }

    if (result) {
      if (this._dropArea) {
        this._dropArea.item.onDropHighlightExit();
      }

      this._dropArea = result;

      const area = this._dropArea.item.getHighlightCoordinates({
        pageX, pageY,
        dropArea: result,
        dragArea: this._dragArea
      });

      this._element.style.transform = `translate(${area.x}px, ${area.y}px)`;
      this._element.style.height = `${area.height}px`;
      this._element.style.width = `${area.width}px`;
    }
  }

  private _onDragStop(e: DragEvent<Renderable>): void {
    if (!this._dropArea) {
      this._fail.next(e.host);
    } else {
      this._areas = null;
      this._element.hidden = true;
      this._item.handleDropCleanup();
      this._dropArea.item.handleDrop(this._item, this._dropArea, e);
      this._success.next(e.host);
    }

    this._dropped.next();
  }

  static isDropTarget(item: any): item is RenderableDropTarget {
    return isObject(item) 
      && item instanceof Renderable
      && isFunction(item['getHighlightCoordinates'])
      && isFunction(item['handleDrop']);
  }
}

import { Inject } from './di';
import { Subject, Observable } from './events';
import { DocumentRef, DragEvent, DragStatus, DragOptions } from './common';
import { propEq } from './utils';

export class Draggable<T> {
  drag: Observable<DragEvent<T>>;

  private _host: T;
  private _drag: Subject<DragEvent<T>> = new Subject();
  private _isDragging: boolean = false;
  private _startX: number = 0;
  private _startY: number = 0;
  private _pastThreshold: boolean = false;
  private _threshold: number = 100;

  static isDraggingEvent = propEq('status', DragStatus.DRAGGING);
  static isDragStartEvent = propEq('status', DragStatus.START);
  static isDragStopEvent = propEq('status', DragStatus.STOP);

  constructor(
    @Inject(DocumentRef) private _document: Document
  ) {
    this.drag = this._drag.asObservable();
    this._onMouseMove = this._onMouseMove.bind(this);
    this._onMouseUp = this._onMouseUp.bind(this);
  }

  get isDragging(): boolean {
    return this._isDragging;
  }

  startDrag(options: DragOptions<T>): void {
    this._host = options.host;
    this._isDragging = true;
    this._startX = options.startX;
    this._startY = options.startY;
    this._threshold = options.threshold || 25;
    this._pastThreshold = false;
    this._document.addEventListener('mousemove', this._onMouseMove, false);
    this._document.addEventListener('mouseup', this._onMouseUp, false);
  }

  destroy(): void {
    this._drag.complete();
  }

  private _onMouseUp(e: MouseEvent): void {
    e.preventDefault();
    e.stopPropagation();

    this._isDragging = false;
    this._document.removeEventListener('mousemove', this._onMouseMove, false);
    this._document.removeEventListener('mouseup', this._onMouseUp, false);

    if (this._pastThreshold) {
      this._drag.next({
        status: DragStatus.STOP,
        x: e.pageX - this._startX,
        y: e.pageY - this._startY,
        pageY: e.pageY,
        pageX: e.pageX,
        host: this._host
      });
    }
  }

  private _onMouseMove(e: MouseEvent): void {
    if (!this._isDragging) {
      return;
    }

    e.preventDefault();
    e.stopPropagation();

    const deltaX = e.pageX - this._startX;
    const deltaY = e.pageY - this._startY;

    if (!this._pastThreshold && (Math.abs(deltaX) >= this._threshold || Math.abs(deltaY) >= this._threshold)) {
      this._pastThreshold = true;  
      this._drag.next({
        host: this._host,
        status: DragStatus.START,
        x: deltaX,
        y: deltaY,
        pageX: e.pageX,
        pageY: e.pageY
      });
    }

    if (this._pastThreshold) {
      this._drag.next({
        status: DragStatus.DRAGGING,
        x: deltaX,
        y: deltaY,
        pageX: e.pageX,
        pageY: e.pageY,
        host: this._host
      });
    }
  }
}

import { Subject, Observable, Observer } from './events';

export const LOCK_DRAGGING = 'dragging';
export const LOCK_RESIZING = 'resizing';

export class LockStateChangeEvent {
  constructor(
    public readonly name: string,
    public readonly value: boolean
  ) {}
}

export class LockState {
  private _map = new Map<string, boolean>();
  private _changes: Subject<LockStateChangeEvent> = new Subject();

  changes: Observable<LockStateChangeEvent> = this._changes.asObservable();

  get isLocked(): boolean {
    return [ ...this._map.values() ].some(Boolean);
  }

  set(name: string, value: boolean): void {
    this._map.set(name, value);
    this._changes.next(new LockStateChangeEvent(name, value));
  }

  get(name: string): boolean {
    return Boolean(this._map.get(name));
  }

  scope(name: string): Observable<boolean> {
    return Observable.create((observer: Observer<boolean>) => {
      if (this._map.has(name)) {
        observer.next(this._map.get(name) as boolean);
      }

      return this.changes
        .filter(e => e.name === name)
        .subscribe(e => observer.next(e.value));
    })
      .distinctUntilChanged();
  }
}

import * as snabbdom from 'snabbdom';
import DOMClass from 'snabbdom/modules/class';
import DOMStyle from 'snabbdom/modules/style';
import DOMProps from 'snabbdom/modules/props';
import DOMEvents from 'snabbdom/modules/eventlisteners';
import DOMAttrs from 'snabbdom/modules/attributes';

import { Injector, ProviderArg, forwardRef } from './di';
import { Renderer, RenderableInjector } from './dom';
import { ViewFactory, ViewManager, ViewLinker, ViewHookExecutor } from './view';
import { DocumentRef, PatchRef } from './common';
import { LayoutManipulator } from './layout';
import { StateContext } from './StateContext';
import { LockState } from './LockState';

const patch = snabbdom.init([
  DOMClass,
  DOMStyle,
  DOMProps,
  DOMEvents,
  DOMAttrs
]);

export class RootInjector extends RenderableInjector {
  constructor(providers: ProviderArg[] = []) {
    super([
      Renderer,
      ViewFactory,
      ViewManager,
      ViewLinker,
      LayoutManipulator,
      StateContext,
      LockState,
      ViewHookExecutor,
      { provide: PatchRef, useValue: patch },
      { provide: DocumentRef, useValue: document },
      { provide: RootInjector, useValue: forwardRef(() => this) },
      ...providers
    ]);
  }
}

import { VNode } from 'snabbdom/vnode';
import h from 'snabbdom/h';

import { Type, Injector, Inject, Optional, forwardRef, ProviderArg } from './di';
import { RootInjector } from './RootInjector';
