  { provide: 'test', useValue: 'blorg' },
  { provide: 'factory', useFactory: () => 'BOOM' },
  { provide: 'myClass', useClass: MyClass },
  MyClass
]);

injector.get('test'); // => 'blorg';
injector.get('factory'); // => 'BOOM';
injector.get('myClass'); // => instanceof MyClass;
injector.get(MyClass); // => instanceof MyClass;

