import { Subject, Observable, BeforeDestroyEvent, BehaviorSubject } from '../events';
import { MakeVisibleCommand, MinimizeCommand } from '../commands';
import { ViewManager } from './ViewManager';
import { ViewFactory } from './ViewFactory';
import { CustomViewHookEvent } from './CustomViewHookEvent';
import { get } from '../utils';
import { StateContext } from '../StateContext';
import { SizeChanges } from './hooks';

/**
 * A renderable that renders a component.
 * @export
 * @class View
 * @extends {Renderable}
 */
export class View extends Renderable {
  protected _viewContainer: ViewContainer<any>;
  protected _visibilityChanges: BehaviorSubject<boolean> = new BehaviorSubject(true);
  protected _sizeChanges: BehaviorSubject<SizeChanges> = new BehaviorSubject({ width: -1, height: -1 });
  protected _viewContainerCreated: Subject<ViewContainer<any>> = new Subject();
  protected _initialCreate: boolean = true;

  /**
   * Notifies when the visibility of this view changes.
   * @type {Observable<boolean>}
   */
  visibilityChanges: Observable<boolean> = this._visibilityChanges.asObservable().distinctUntilChanged();
  /**
   * Notifies when the dimensions of this view changes.
   * @type {Observable<{ width: number, height: number }>}
   */
  sizeChanges: Observable<SizeChanges> = this._sizeChanges.asObservable();

  /**
   * Notifies when the view container is resolved.
   * @type {Observable<ViewContainer<any>>}
   */
  viewContainerCreated: Observable<ViewContainer<any>> = this._viewContainerCreated.asObservable();

  /**
   * Creates an instance of View.
   * @param {Renderable} _container 
   * @param {ViewConfig} _configuration 
   * @param {ViewManager} _viewManager 
   * @param {ViewFactory} _viewFactory 
   * @param {Document} _document 
   */
  constructor(
    @Inject(ContainerRef) protected _container: Renderable,
    @Inject(ConfigurationRef) protected _configuration: ViewConfig,
    @Inject(ViewManager) protected _viewManager: ViewManager,
    @Inject(ViewFactory) protected _viewFactory: ViewFactory,
    @Inject(DocumentRef) protected _document: Document,
    @Inject(StateContext) protected _stateContext: StateContext
  ) {
    super();
  }

  get width(): number {
    return this._container.width;
  }

  get height(): number {
    return this._container.height;
  }

  /**
   * Whether this view is configured to be lazy.
   * @readonly
   * @type {(boolean|null)}
   */
  get lazy(): boolean|null {
    return this.resolveConfigProperty<boolean>('lazy');
  }

  /**
   * Whether this view in configured to be cacheable.
   * @readonly
   * @type {(boolean|null)}
   */
  get isCacheable(): boolean {
    const cacheStrategy = this.caching;

    if ((cacheStrategy === CacheStrategy.RELOAD && this._stateContext.context === ContextType.NONE) 
      || cacheStrategy === CacheStrategy.PERSISTENT) {
      return true;
    }

    return false;
  }

  get caching(): CacheStrategy|null {
    return this.resolveConfigProperty<CacheStrategy>('caching');
  }

  /**
   * The 'ref' string this view is configured with.
   * @readonly
   * @type {(string|null)}
   */
  get ref(): string|null {
    return this.resolveConfigProperty<string>('ref');
  }

  /**
   * The resolution strategy this view is configured with.
   * @readonly
   * @type {(ResolverStrategy|null)}
   */
  get resolution(): ResolverStrategy|null {
    return this.resolveConfigProperty<ResolverStrategy>('resolution');
  }

  /**
   * The token this view is using for registration.
   * @readonly
   * @type {(any|null)}
   */
  get token(): any|null {
    return this._viewFactory.getTokenFrom(this._configuration);
  }

  initialize(): void {
    super.initialize();

    this._renderer.rendered
      .takeUntil(this.destroyed)
      .subscribe(this._postRender.bind(this));
  }

  render(): VNode {
    return h('div.ug-layout__view-container', {
      key: this.uid,
      style: {
        width: `${this.width}px`,
        height: `${this.height}px`
      },
      hook: {
        create: (oldNode, newNode) => this._onCreate(newNode.elm as HTMLElement)
      }
    });
  }

  destroy(): void {
    this._sizeChanges.complete();
    this._visibilityChanges.complete();

    super.destroy();
  }

  /**
   * Closes this view.
   * @emits {BeforeDestroyEvent} Fired when not silent.
   * @param {{ silent?: boolean }} [args={}] 
   */
  close(args: { silent?: boolean } = {}): void {
    const { silent = false } = args;

    if (!silent) {
      const event = new BeforeDestroyEvent(this);

      this._eventBus.next(event);
      event.results().subscribe(() => this.remove());
    } else {
      this.destroy();
    }
  }

  /**
   * Makes this view visible.
   * @emits {MakeVisibileCommand}
   */
  makeVisible(): void {
    this.emitUp(new MakeVisibleCommand(this));
  }

  /**
   * Minimizes this view if applicable.
   * @emits {MinimizeCommand}
   */
  minimize(): void {
    this.emitUp(new MinimizeCommand(this));
  }

  /**
   * Resolves a views config property. Checks the configuration given to the
   * view renderable first then checks the component metadata.
   * @template T The return type.
   * @param {string} path 
   * @returns {(T|null)} 
   */
  resolveConfigProperty<T>(path: string): T|null {
    return this._viewFactory.resolveConfigProperty<T>(this._configuration, path);
  }

  /**
   * Invoked on every render cycle.
   * @private
   */
  private _postRender(): void {
    // Check for these changes every render iteration.
    this._visibilityChanges.next(this.isVisible());
    this._sizeChanges.next({ width: this.width, height: this.height });
  }

  /**
   * Invoked when snabbdom has created the HTML element for this view.
   * @private
   * @param {HTMLElement} element 
   */
  private _onCreate(element: HTMLElement): void {
    if (!this._viewContainer) {
      let container = this._viewManager.resolve<any>(this._configuration);

      if (container) {
        if (container.hasComponent) {
          container.resolve({ fromCache: true });
        }
      } else {
        container = this._viewManager.create({
          config: this._configuration,
          injector: this.injector
        });
      }

      this._viewContainer = container;
      this._viewContainerCreated.next(this._viewContainer);
    }

    this._viewContainer.setView(this);
    this._viewContainer.mountTo(element);
  }

  /**
   * Configures a view.
   * @static
   * @param {ViewConfig} config 
   * @returns {ConfiguredRenderable<View>} 
   */
  static configure(config: ViewConfig): ConfiguredRenderable<View> {
    return new ConfiguredRenderable(View, config);
  }
}

import { Injector, Type, Inject, Optional } from '../di';
import { Renderable } from '../dom';
import { ReplaySubject, Observable, Subject, BeforeDestroyEvent, BehaviorSubject } from '../events';
import { ContainerRef, ConfigurationRef, DocumentRef } from '../common';
import { View } from './View';
import { ViewComponentRef } from './common';
import { CustomViewHookEvent } from './CustomViewHookEvent';
import { ViewHookExecutor, ViewHookMetadata } from './hooks';
import { isFunction, get, uid, eq, isPromise, isObject, Deferred } from '../utils';

export enum ViewContainerStatus {
  READY,
  PENDING,
  FAILED
}

export enum ViewContainerAttachedStatus {
  ATTACHED,
  DETACHED
}

export interface ViewContainerReadyOptions {
  /**
   * Whether to initiale the component.
   * @type {boolean}
   */
  init?: boolean;
  /**
   * Statuses that tell when the component is ready.
   * @type {ViewContainerStatus[]}
   */
  when?: ViewContainerStatus[];
  /**
   * When the view transitions to one of these statuses then it will stop listening for status
   * updates and the observable will complete without the component being emitted as ready.
   * @type {ViewContainerStatus[]}
   */
  until?: ViewContainerStatus[];
}

/**
 * A container that holds a 1 to 1 relationship with a component instance. This instance
 * is the main API for a component to interact with the layout framework.
 * @export
 * @class ViewContainer
 * @template T The component type.
 */
export class ViewContainer<T> {
  /**
   * This element is the mount point that views can mount to.
   * The node created by the render method gets recreated when moved
   * somewhere else in the tree. This element is constant.
   * @private
   * @type {HTMLElement}
   */
  private _element: HTMLElement = this._document.createElement('div');
  private _component: T|null = null;
  private _container: View|null;
  private _status: BehaviorSubject<ViewContainerStatus> = new BehaviorSubject(ViewContainerStatus.PENDING);
  private _destroyed: Subject<ViewContainer<T>> = new Subject();
  private _beforeDestroy: Subject<BeforeDestroyEvent<Renderable>> = new Subject();
  private _containerChange: Subject<View|null> = new Subject<View|null>();
  private _visibilityChanges: BehaviorSubject<boolean> = new BehaviorSubject(true);
  private _sizeChanges: BehaviorSubject<{ width: number, height: number }> = new BehaviorSubject({ width: -1, height: -1 });
  private _attached: Subject<boolean> = new Subject();
  private _initialized: BehaviorSubject<boolean> = new BehaviorSubject<boolean>(false);
  private _componentReady: BehaviorSubject<boolean> = new BehaviorSubject<boolean>(false);
  private _componentInitialized: BehaviorSubject<boolean> = new BehaviorSubject<boolean>(false);
  private _isInitialized: boolean = false;
  private _retry: Function|null = null;
  private _statusInternal: ViewContainerStatus;

  /**
   * A unique identifier for this instance.
   * @type {number}
   */
  readonly id: number = uid();
  /**
   * Notifies that the view is being destroyed. This action is cancellable or can be halted until
   * an async action is complete.
   * @see Cancellable
   * @type {Observable<BeforeDestroyEvent<Renderable>>}
   */
  beforeDestroy: Observable<BeforeDestroyEvent<Renderable>> = this._beforeDestroy.asObservable();
  /**
   * Notifies when the view is destroyed.
   * @type {Observable<ViewContainer<T>>}
   */
  destroyed: Observable<ViewContainer<T>> = this._destroyed.asObservable();
  /**
   * Notifies when the visibility of this view changes.
   * @type {Observable<boolean>}
   */
  visibilityChanges: Observable<boolean> = this._visibilityChanges.asObservable();
  /**
   * Notifies when the dimensions of this views container has changed.
   * @type {Observable<{ width: number, height: number }>}
   */
  sizeChanges: Observable<{ width: number, height: number }> = this._sizeChanges
    .asObservable()
    .filter(e => e.height !== -1 && e.width !== -1)
    .distinctUntilChanged((p, c) => p.width === c.width && p.height === c.height)
  /**
   * Notifies when the status of this component changes.
   * @type {Observable<ViewContainerStatus>}
   */
  status: Observable<ViewContainerStatus> = this._status.asObservable();
  /**
   * Contains the state of ths views initialized state.
   * @type {Observable<boolean>}
   */
  initialized: Observable<boolean> = this._initialized.asObservable();
  /**
   * Notifies when this container has changed the {@link View} it is associated with.
   * @type {(Observable<View|null>)}
   */
  containerChange: Observable<View|null> = this._containerChange.asObservable();
  /**
   * Notifies when the view has become attached.
   * @type {Observable<boolean>}
   */
  attached: Observable<boolean> = this._attached.asObservable().filter(eq(true));
  /**
   * Notifies when the view has become detached.
   * @type {Observable<boolean>}
   */
  detached: Observable<boolean> = this._attached.asObservable().filter(eq(false));
  /**
   * Notifies when the component is assigned to the container and ready for access.
   * @type {Observable<boolean>}
   */
  componentReady: Observable<boolean> = this._componentReady.asObservable();
  /**
   * Notifies when the component is initialized.
   * @type {Observable<boolean>}
   */
  componentInitialized: Observable<boolean> = this._componentInitialized.asObservable();

  /**
   * Creates an instance of ViewContainer.
   * @param {Document} _document 
   * @param {Injector} _injector 
   */
  constructor(
    @Inject(DocumentRef) protected _document: Document,
    @Inject(Injector) protected _injector: Injector,
    @Inject(ViewHookExecutor) protected _viewHookExecutor: ViewHookExecutor
  ) {
    this._element.classList.add('ug-layout__view-container-mount');

    this.attached.subscribe(() => this._executeHook('ugOnAttach', this._container));
    this.detached.subscribe(() => this._executeHook('ugOnDetach'));
    this.sizeChanges.subscribe(dimensions => this._executeHook('ugOnSizeChange', dimensions));
    this.visibilityChanges.subscribe(isVisible => this._executeHook('ugOnVisibilityChange', isVisible));
    this.beforeDestroy.subscribe(event => this._executeHook('ugOnBeforeDestroy', event));
    this.initialized.subscribe(v => this._isInitialized = v);

    // Reset the retry function when the component is no longer failed.
    this.status.filter(eq(ViewContainerStatus.READY)).subscribe(() => this._retry = null);
    this.status.subscribe(s => this._statusInternal = s);
  }

  get hasComponent(): boolean {
    return Boolean(this._component);
  }

  /**
   * Current width in pixels.
   * @readonly
   * @type {number}
   */
  get width(): number {
    return get(this._container, 'width', 0);
  }

  /**
   * Current height in pixels.
   * @readonly
   * @type {number}
   */
  get height(): number {
    return get(this._container, 'height', 0);
  }

  /**
   * The component instance associated with this container.
   * @readonly
   * @type {T|null}
   */
  get component(): T|null {
    return this._component;
  }

  /**
   * Whether the container is cacheable.
   * @readonly
   * @type {boolean}
   */
  get isCacheable(): boolean {
    return this._container ? this._container.isCacheable : false;
  }

  get isLazy(): boolean {
    return this._container ? Boolean(this._container.lazy) : false;
  }

  /**
   * The HTML element for this container.
   * @readonly
   * @type {HTMLElement}
   */
  get element(): HTMLElement {
    return this._element;
  }

  get view(): View|null {
    return this._container;
  }

  /**
   * Get's a token from this containers injector. Note, this should not be used to grab
   * parent renderables or any item that can be changed.
   * @template U The return type.
   * @param {*} token 
   * @returns {(U|null)} 
   */
  get<U>(token: any): U|null {
    return this._injector.get(token, null);
  }

  /**
   * Destroys the container.
   */
  destroy(): void {
    this._destroyed.next();

    this._destroyed.complete();
    this._status.complete();
    this._beforeDestroy.complete();
    this._containerChange.complete();
    this._visibilityChanges.complete();
    this._sizeChanges.complete();
    this._initialized.complete();
  }

  /**
   * Waits for the component to be ready. If the component is not initialized
   * it will be initialized. This is important when the view is lazy and hasn't
   * been shown yet but we need to interact with the component.
   * @async
   * @param {{ init?: boolean }} [options={}] 
   * @returns {Promise<ViewContainer<T>>} 
   */
  ready(options: ViewContainerReadyOptions = {}): Observable<ViewContainer<T>> {
    return Observable.create(observer => {
      const { 
        init = true, 
        when = [ ViewContainerStatus.READY, ViewContainerStatus.FAILED ],
        until = []
      } = options;

      if (!this._isInitialized && init !== false) {
        this.initialize();  
      }

      return this.status
        .subscribe(status => {
          if (when.indexOf(status) !== -1) {
            observer.next(this);
            observer.complete();
          } else if (until.indexOf(status) !== -1) {
            observer.complete();
          }
        });
    });
  }

  /**
   * Initializes the component. This creates the component. 
   */
  initialize(): void {
    if (this._isInitialized) {
      return;
    }

    const component = this._injector.get(ViewComponentRef);

    if (isPromise<T>(component)) {
      component.then(val => this._onComponentReady(val));
    } else {
      this._onComponentReady(component);
    }

    this._initialized.next(true);
  }

  /**
   * Sets the containing {@link View} for this container.
   * @param {(View|null)} container 
   * @returns {void} 
   */
  setView(container: View|null): void {
    if (container === this._container) {
      return;
    }

    this._container = container;

    // This needs to fire before we wire up to the new container.
    this._containerChange.next(container);

    if (this._container) {
      this._container.destroyed
        .takeUntil(this.containerChange)
        .subscribe(() => this._onViewDestroy());

      this._container
        .scope(BeforeDestroyEvent)
        .takeUntil(this.containerChange)
        .subscribe(e => this._beforeDestroy.next(e));

      this._container.visibilityChanges
        .takeUntil(this.containerChange)
        .subscribe(e => this._visibilityChanges.next(e));

      this._container.sizeChanges
        .takeUntil(this.containerChange)
        .subscribe(e => this._sizeChanges.next(e));

      this._container
        .scope(CustomViewHookEvent)
        .takeUntil(this.containerChange)
        .subscribe(e => this._onCustomViewHook(e));
    }

    this._attached.next(Boolean(this._container));
  }

  /**
   * Gets this renderables parent or any parent that is
   * an instance of the passed in constructor. If non is found
   * then null is returned.
   * @template T The constructor type.
   * @param {Type<T>} [Ctor] 
   * @returns {(T|null)} 
   */
  getParent<U extends Renderable>(Ctor: Type<U>): U|null {
    return this._container ? this._container.getParent(Ctor) : null;
  }

  /**
   * Gets this renderables parents or any parents that are
   * an instance of the passed in constructor.    
   * @template T The constructor type.
   * @param {Type<T>} [Ctor] 
   * @returns {(T|null)} 
   */
  getParents<U extends Renderable>(Ctor: Type<U>): U[] {
    return this._container ? this._container.getParents(Ctor) : [];
  }

  /**
   * Signals up the tree to make this {@link View} visible.
   */
  makeVisible(): void {
    if (this._container) {
      this._container.makeVisible();
    }
  }

  /**
   * Determines whether this renderable is visible.
   * @returns {boolean} 
   */
  isVisible(): boolean {
    return this._container ? this._container.isVisible() : false;
  }

  /**
   * Closes this view.
   * @param {{ silent?: boolean }} args 
   */
  close(args: { silent?: boolean }): void {
    if (this._container) {
      this._container.close(args);
    }
  }

  /**
   * Mounts this containers element to the given element.
   * @param {HTMLElement} element 
   */
  mountTo(element: HTMLElement): void {
    element.appendChild(this._element);
  }

  /**
   * Mounts an element to this containers element.
   * @param {HTMLElement} element 
   */
  mount(element: HTMLElement): void {
    this._element.appendChild(element);
  }

  /**
   * Sets all the content of this containers element to the given HTML string.
   * @param {string} html 
   */
  mountHTML(html: string): void {
    this._element.innerHTML = html;
  }

  /**
   * Detaches this container from it's view.
   */
  detach(): void {
    this.setView(null);
  }

  fail(retry?: Function|null): void {
    // Can be nulled with explicit null value
    if (retry || retry === null) {
      this._retry = retry;
    }

    this._status.next(ViewContainerStatus.FAILED);
  }

  retry(): void {
    if (this._retry) {
      this._retry();
    }
  }

  async resolve(options: { fromCache?: boolean } = {}): Promise<void> {
    const { fromCache = false } = options;

    if (!this.hasComponent) {
      throw new Error('Can not resolve container without component being ready.');
    }

    this._status.next(ViewContainerStatus.PENDING);

    try {
      await this._executeHook('ugOnResolve', { fromCache });
      this._status.next(ViewContainerStatus.READY);
    } catch(e) {
      this.fail(() => this.resolve(options));
      throw e;
    }
  }

  /**
   * Invoked when the view renderable is destroyed.
   * @private
   */
  private _onViewDestroy(): void {
    // If this view is cacheable, we don't destroy it.
    if (this.isCacheable) {
      this.detach();
    } else {
      this.destroy();
    }
  }

  /**
   * Executes a hook on the component.
   * @private
   * @param {string} name 
   * @param {...any[]} args 
   * @returns {*} 
   */
  private _executeHook(name: string, arg?: any): any {
    if (this._component) {
      return this._viewHookExecutor.execute<T>(this._component, name, arg);
    }
  }

  private _onCustomViewHook(event: CustomViewHookEvent<any>): void {
    if (event.execute) {
      event.execute(this, this._viewHookExecutor, event);
    } else {
      this._executeHook(event.name, event.arg);
    }
  }

  /**
   * Invoked when the component is resolved from the initialization.
   * @private
   * @async
   * @param {T} component 
   * @returns {Promise<void>} 
   */
  private async _onComponentReady(component: T): Promise<void> {
    this._component = component;

    // Link up any hooks right when the component is instantiated.
    this._viewHookExecutor.linkObservers(this, this._component.constructor.prototype);
    this._componentReady.next(true);

    if (this._statusInternal === ViewContainerStatus.FAILED) {
      return;
    }

    await this.resolve();

    this._executeHook('ugOnInit', this);
    this._componentInitialized.next(true);
  }
}

import {
  ViewComponentConfig, 
  ViewConfig, 
  ViewComponentRef, 
  VIEW_CONFIG_KEY
} from './common';
import { Injector, Inject, Optional, ProviderArg, Type } from '../di';
import { View } from './View';
import { ViewContainer } from './ViewContainer';
import { ElementRef, ContainerRef } from '../common';
import { get, isFunction } from '../utils';

export interface ViewFactoryArgs {
  config: ViewConfig;
  injector?: Injector;
}

/**
 * Responsible for creating view containers from view configurations.
 * @export
 * @class ViewFactory
 */
export class ViewFactory {
  constructor(
    @Inject(Injector) private _injector: Injector
  ) {}

  /**
   * Creates a view container from the given configuration.
   * @template T The component type.
   * @param {ViewFactoryArgs} args 
   * @returns {ViewContainer<T>} 
   */
  create<T>(args: ViewFactoryArgs): ViewContainer<T> {
    let { config } = args;
    const { injector = this._injector } = args;
    const isLazy = this.resolveConfigProperty(config, 'lazy');
    const providers: ProviderArg[] = [ this._getViewContainerProvider(config) ];

    if (config.useFactory) {
      providers.push({
        provide: ViewComponentRef, 
        useFactory: config.useFactory,
        deps: config.deps
      });
    } else if (config.useClass) {
      providers.push({ provide: ViewComponentRef, useClass: config.useClass });
    } else if (config.useValue) {
      providers.push({ provide: ViewComponentRef, useValue: config.useValue });
    } else {
      throw new Error('View config has no creatable view.');
    }

    const viewInjector = Injector.fromInjectable(ViewContainer, providers, injector);
    const viewContainer = viewInjector.get(ViewContainer) as ViewContainer<any>;

    viewInjector.registerProvider({ provide: ElementRef, useValue: viewContainer.element });

    // If this view is lazy and it is not already visible wait for the
    // view to become visible before initializing the view container.
    if (isLazy) {
      viewContainer.visibilityChanges
        .first(() => viewContainer.isVisible(), v => v, false)
        .filter(Boolean)
        .delay(0)
        .subscribe(() => viewContainer.initialize());
    } else {
      viewContainer.initialize();
    }

    return viewContainer;
  }  

  /**
   * Gets the token from a view config. If using `useClass` the class will be used as the token.
   * Any other type will require token to be provided.
   * @param {ViewConfig} config 
   * @returns {*} 
   */
  getTokenFrom(config: ViewConfig): any {
    if (config.token) {
      return config.token;
    }

    if (config.useClass) {
      return config.useClass;
    } else if (config.useFactory) {
      return config.useFactory;
    } else if (config.useValue) {
      return config.useValue;
    }

    throw new Error('Can not resolve token from config.');
  }

  /**
   * Resolves a config property by looking at the view config then the components metadata config.
   * @template T The type of the value expected.
   * @param {ViewConfig} config 
   * @param {string} path 
   * @returns {(T|null)} 
   */
  resolveConfigProperty<T>(config: ViewConfig, path: string): T|null {
    const token = this.getTokenFrom(config);
    let result = get(config, path);

    if (result !== undefined) {
      return result as T;
    }

    return this.resolveMetaConfigProperty<T>(token, path);
  }

  /**
   * Resolves a config property from the tokens metadata.
   * @template T The return type.
   * @param {*} token 
   * @param {string} path 
   * @returns {(T|null)} 
   */
  resolveMetaConfigProperty<T>(token: any, path: string): T|null {
    const metadata = Reflect.getOwnMetadata(VIEW_CONFIG_KEY, token) as ViewComponentConfig|undefined;
    const result = get(metadata, path);

    if (result !== undefined) {
      return result as T;
    }

    return null;
  }

  destroy(): void {}

  private _getViewContainerProvider(config: ViewConfig): ProviderArg {
    let viewContainerProvider = this.resolveMetaConfigProperty<ProviderArg>(this.getTokenFrom(config), 'container') || ViewContainer;

    if (isFunction(viewContainerProvider)) {
      viewContainerProvider = { provide: ViewContainer, useClass: viewContainerProvider };
    }

    return viewContainerProvider;
  }
}

import { Inject, Injector } from '../di';
import { ViewManager } from './ViewManager';
import { 
  VIEW_LINKER_METADATA, 
  ViewQueryConfig, 
  ViewQueryReadType, 
  ViewLinkerMetadata,
  ViewInsertConfig,
  ViewResolveConfig,
  ViewQueryReadOptions
} from './common';
import { ViewContainer, ViewContainerStatus } from './ViewContainer';
import { Subscription, Observable, Observer, Subject } from '../events';
import { get, isFunction, isObject } from '../utils';
import { LayoutManipulator } from '../layout';
import { getDefaultMetadata } from './decorators';

/**
 * This service wires up a class instance with custom behavior for dealing with communication
 * between multiple views. The instance is usually decorated with decorators that describe the
 * actions needing to be performed. Such actions are resolving a view, inserting {@link Renderable}s into
 * the current layout, or querying for specific views and interacting with them.
 * 
 * Due to the async nature of component resolution, it is important to never store the component instances
 * as they could change over the course of the layouts lifecycle. This is why we set up a `ViewResolve` to
 * get the component we want.
 * @export
 * @class ViewLinker
 * @example 
 * 
 * class MyController {
 *   @ViewResolve({ token: SomeOtherComponent }) 
 *   getSomeOtherComponent: Observable<SomeOtherComponent>;
 * 
 *   @ViewQuery({ token: MyComponent })
 *   onMyViewResolved(component: MyComponent): void {
 *     // MyComponent has stream for selecting something.
 *     component.somethingSelected.subscribe(thing => {
 *       // When MyComponent selected something, set it on SomeOtherComponent.
 *       this.getSomeOtherComponent().subscribe(otherComp => {
 *         otherComp.setSomething(thing);
 *       });
 *     });
 *   }
 * }
 * 
 * // Lets assume I have the viewLinker instance from the root layout I'm working with.
 * 
 * const myController = new MyController();
 * 
 * viewLinker.autowire(myController);
 */
export class ViewLinker {
  constructor(
    @Inject(ViewManager) private _viewManager: ViewManager,
    @Inject(Injector) private _injector: Injector,
    @Inject(LayoutManipulator) private _manipulator: LayoutManipulator
  ) {}

  /**
   * Wires all types of custom behavior for linker
   * @param {object} instance 
   * @returns {Subscription} A subscription for all streams created.
   */
  autowire(instance: object): Subscription {
    const metadata = this.readMetadata(instance);
    const subscription = new Subscription();

    for (const init of metadata.inits) {
      this._injector.invoke((...args) => instance[init.method](...args), init.injections);
    }

    for (const insert of metadata.inserts) {
      this.wireInsert(instance, insert);
    }

    for (const resolve of metadata.resolves) {
      this.wireResolve(instance, resolve);
    }

    for (const query of metadata.queries) {
      subscription.add(this.wireQuery(instance, query));
    }

    return subscription;
  }

  /**
   * Wires a query on the instance using the provided config.
   * @param {object} instance 
   * @param {ViewQueryConfig} config 
   * @returns {Subscription} 
   */
  wireQuery(instance: object, config: ViewQueryConfig): Subscription {
    const { read, method } = config;
    const subscription = new Subscription();
    const _unsubscribed = new Subject<void>();
    const unsubscribed = _unsubscribed.asObservable();

    subscription.add(() => {
      _unsubscribed.next();
      _unsubscribed.complete();
    });

    if (!isFunction(instance[method])) {
      throw new Error(`Can not wire method '${method}'. Method does not exist.`);
    }

    subscription.add(
      this.readQuery(this._viewManager.subscribeToQuery(config), read)
        .subscribe(arg => subscription.add(instance[method](arg, unsubscribed)))
    );

    return subscription;
  }

  /**
   * Wires a view insert on the instance using the provided config.
   * @param {object} instance 
   * @param {ViewInsertConfig} config 
   */
  wireInsert(instance: object, config: ViewInsertConfig): void {
    Object.defineProperty(instance, config.method, {
      enumerable: false,
      configurable: true,
      writable: true,
      value: this._insert.bind(this, instance, config)
    })
  }


  /**
   * Wires a resolve method with the given config.
   * @param {object} instance 
   * @param {ViewResolveConfig} config 
   */
  wireResolve(instance: object, config: ViewResolveConfig): void {
    Object.defineProperty(instance, config.method, {
      enumerable: false,
      configurable: true,
      writable: true,
      value: this._resolve.bind(this, instance, config)
    })
  }

  /**
   * Reads metadata from a class instance.
   * @param {object} instance 
   * @returns {ViewLinkerMetadata} 
   */
  readMetadata(instance: object): ViewLinkerMetadata {
    return ViewLinker.readMetadata(get(instance, 'constructor.prototype', null));
  }

  /**
   * Reads a query observable from the view manager. A `ViewQuery` decorated method can be given a different
   * argument depending on how you need to interact with the view. If we need the component then the 
   * query will wait until the component is in a ready state. If we need the {@link ViewContainer} then it 
   * will be given to us regardless of whether the component is ready or not. We can also request the query
   * observable instead if we want some custom resolution logic.
   * @template T 
   * @param {Observable<ViewContainer<T>>} query 
   * @param {(ViewQueryReadType|ViewQueryReadOptions)} [options] 
   * @returns {Observable<any>} 
   */
  readQuery<T>(query: Observable<ViewContainer<T>>, options?: ViewQueryReadType|ViewQueryReadOptions): Observable<any> {
    const _options = (isObject(options) ? options : { type: options }) as ViewQueryReadOptions;
    const {
      type = ViewQueryReadType.COMPONENT, 
      when = [ ViewContainerStatus.READY ], 
      until = [ ViewContainerStatus.FAILED ],
      lazy = true
    } = _options;

    return Observable.create((observer: Observer<any>) => {
      if (type === ViewQueryReadType.OBSERVABLE) {
        observer.next(query);
        observer.complete();

        return;
      } 

      return query.subscribe(container => {
        if (type === ViewQueryReadType.COMPONENT) {
          container.ready({ when, until, init: !lazy }).subscribe({
            next: () => observer.next(container.component),
            complete: () => observer.complete()
          });
        } else {
          observer.next(container);
          observer.complete();
        }
      });
    });
  }

  private _insert<T>(instance: object, config: ViewInsertConfig): Observable<any> {
    const from = this._viewManager.query<T>(config.from)[0];
    const view = from ? from.view : null;
    const { query, read } = config;

    return Observable.create((observer: Observer<any>) => {
      if (view) {
        this._manipulator.insert({ ...config, from: view }).subscribe(() => {
          this.readQuery(this._viewManager.subscribeToQuery(query), read).subscribe(observer);
        });  
      } else {
        observer.complete();
      }
    });
  }

  private _resolve<T>(instance: object, config: ViewResolveConfig, options: ViewQueryReadOptions = {}): Observable<any> {
    const { query, read } = config;
    const _options = {
      lazy: false,
      ...(isObject(read) ? read : { type: read }),
      ...options
    };

    return this.readQuery(this._viewManager.subscribeToQuery(query), _options);
  }

  static readMetadata(target: any): ViewLinkerMetadata {
    if (!target) {
      return getDefaultMetadata();
    }

    return Reflect.getOwnMetadata(VIEW_LINKER_METADATA, target) || getDefaultMetadata();
  }
}

