import { Inject, Type } from '../di';
import { ViewFactory, ViewFactoryArgs } from './ViewFactory';
import { ViewContainer } from './ViewContainer';
import { Subject, Observable, Observer } from '../events';
import {
  VIEW_CONFIG_KEY, 
  ViewConfig, 
  ViewComponentConfig, 
  ResolverStrategy,
  ViewQueryArgs
} from './common';
import { propEq } from '../utils';

export interface ViewManagerEvent<T> {
  ref?: string|null;
  token: any;
  container: ViewContainer<T>;
}

export interface RefChangeEvent<T> {
  ref: string;
  container: ViewContainer<T>;
  token: any;
  type: 'add'|'remove';
}

export interface ViewResolutionOptions {
  emit?: boolean;
}

export class ViewManager {
  private _views: Map<any, { [key: string]: ViewContainer<any> }> = new Map();
  private _refs: Map<string, ViewContainer<any>> = new Map();
  private _resolved: Subject<ViewManagerEvent<any>> = new Subject();
  private _created: Subject<ViewManagerEvent<any>> = new Subject();
  private _destroyed: Subject<void> = new Subject<void>();
  private _refChanges: Subject<RefChangeEvent<any>> = new Subject();
  private _viewInit: Subject<ViewManagerEvent<any>> = new Subject();

  resolved: Observable<ViewManagerEvent<any>> = this._resolved.asObservable();
  created: Observable<ViewManagerEvent<any>> = this._created.asObservable();
  destroyed: Observable<void> = this._destroyed.asObservable();
  refChanges: Observable<RefChangeEvent<any>> = this._refChanges.asObservable();
  viewInit: Observable<ViewManagerEvent<any>> = this._viewInit.asObservable();

  constructor(
    @Inject(ViewFactory) private _viewFactory: ViewFactory
  ) {}

  has(token: any, id?: number): boolean {
    const map = this.getAll(token);

    if (!map) {
      return false;
    }

    if (id != null) {
      return map.hasOwnProperty(id);
    }

    return true;
  }

  get<T>(token: any, id: number): ViewContainer<T>|null {
    const map = this.getAll(token);

    if (!map) {
      return null;
    }

    return map.hasOwnProperty(id) ? map[id] : null;
  }

  getAll(token: any): ({ [key:number]: ViewContainer<any> })|null {
    return this._views.get(token) || null;
  }

  destroy() {
    for (const views of this._views.values()) {
      for (const key of Object.keys(views)) {
        const view = views[key] as ViewContainer<any>|null;

        if (view) {
          view.destroy();  
        }
      }
    }      

    this._destroyed.next();
    this._destroyed.complete();
    this._created.complete();
    this._resolved.complete();
    this._refChanges.complete();
    this._viewInit.complete();
  }

  getRef<T>(ref: string): ViewContainer<T>|null {
    return this._refs.get(ref) || null;
  }

  resolve<T>(config: ViewConfig, options: ViewResolutionOptions = {}): ViewContainer<T>|null {
    const token = this._viewFactory.getTokenFrom(config);
    const metadata = this._assertAndReadComponent(token);

    return this._resolve<T>(config, metadata, options);
  }

  resolveOrCreate<T>(args: ViewFactoryArgs, options: ViewResolutionOptions = {}): ViewContainer<T> {
    return this.resolveOrCreateWith<T>(args.config, (): ViewContainer<T> => {
      return this._viewFactory.create<T>(args);
    }, options);
  }

  resolveOrCreateWith<T>(config: ViewConfig, factory: (viewFactory: ViewFactory) => ViewContainer<T>, options: ViewResolutionOptions = {}): ViewContainer<T> {
    const token = this._viewFactory.getTokenFrom(config);
    const metadata = this._assertAndReadComponent(token);

    const container = this._resolve<T>(config, metadata, options);

    if (container) {
      return container;
    }

    return this.createWith<T>(config, factory, options);
  }

  create<T>(args: ViewFactoryArgs, options: ViewResolutionOptions = {}): ViewContainer<T> {
    return this.createWith(args.config, (): ViewContainer<T> => {
      return this._viewFactory.create<T>(args);
    }, options);
  }

  createWith<T>(config: ViewConfig, factory: (viewFactory: ViewFactory) => ViewContainer<T>, options: ViewResolutionOptions = {}): ViewContainer<T> {
    const token = this._viewFactory.getTokenFrom(config);

    this._assertAndReadComponent(token);

    const container = factory(this._viewFactory);

    this._created.next({
      container,
      token,
      ref: config.ref
    });

    this.register(token, container, { ref: config.ref });

    return container;
  }

  register(token: any, container: ViewContainer<any>, options: { ref?: string|null } = {}): void {
    const { ref } = options;

    if (!this._views.has(token)) {
      this._views.set(token, {});
    }

    const map = this._views.get(token) as { [key:number]: ViewContainer<any> };

    if (map.hasOwnProperty(container.id)) {
      throw new Error(`An view instance entry already exists for ${token} ${container.id}`);
    }

    map[container.id] = container;

    this._views.set(token, map);

    container.destroyed.subscribe(() => this.unregister(token, container, { ref }));

    if (ref) {
      if (this._refs.has(ref)) {
        throw new Error(`Ref '${ref}' already exists.`);
      }

      this._refs.set(ref, container);
      this._refChanges.next({ ref, container, token, type: 'add' });
    }
  }

  unregister(token: any, container: ViewContainer<any>, options: { ref?: string|null } = {}): void {
    const { ref } = options;
    const map = this._views.get(token) as { [key:number]: ViewContainer<any> };

    if (!map) {
      throw new Error(`No entries exist for token ${token}`);
    }

    if (!map.hasOwnProperty(container.id)) {
      throw new Error(`An view instance entry already exists for ${token} ${container.id}`);
    }

    delete map[container.id];

    if (Object.keys(map).length === 0) {
      this._views.delete(token);
    }

    if (ref && this._refs.has(ref)) {
      this._refs.delete(ref);
      this._refChanges.next({ ref, container, token, type: 'remove' });
    }
  }

  private _resolve<T>(config: ViewConfig, metadata: ViewComponentConfig, options: ViewResolutionOptions): ViewContainer<T>|null {
    const resolution = this._viewFactory.resolveConfigProperty(config, 'resolution');
    let result: ViewContainer<T>|null = null;

    // REF
    if (resolution === ResolverStrategy.REF) {
      if (config.ref && this._refs.has(config.ref)) {
        result = this.getRef(config.ref) as ViewContainer<T>;
      }
    } else {
      // SINGLETON
      const token = this._viewFactory.getTokenFrom(config);
      const views = this.getAll(token);

      if (views) {
        const keys = Object.keys(views);

        if (keys.length) {
          result = views[keys[0]];
        }
      }
    }

    if (result && options.emit !== false) {
      this._resolved.next({
        ref: config.ref,
        container: result,
        token: this._viewFactory.getTokenFrom(config)
      });
    }

    return result;
  }

  /**
   * Notifies when the a view is registered matching the given query.
   * @template T 
   * @param {{ token?: any, ref?: string }} [query={}] 
   * @returns {Observable<ViewContainer<T>>} 
   */
  subscribeToQuery<T>(query: ViewQueryArgs = {}): Observable<ViewContainer<T>> {
    const { ref, token, id } = query;

    if (ref) {
      return this.queryRef(ref);
    } else if (token) {
      return this.queryToken(token, id);
    }

    return Observable.empty();
  }

  queryToken<T>(token: any, id?: number): Observable<ViewContainer<T>> {
    return Observable.create((observer: Observer<ViewContainer<T>>) => {
      for (const container of this.query<T>({ token, id })) {
        observer.next(container);
      }

      return this.viewInit
        .filter(propEq('token', token))
        .filter(e => id ? id === e.container.id : true)
        .map(e => e.container)
        .subscribe(observer);
    });
  }

  queryRef<T>(ref: string): Observable<ViewContainer<T>> {
    return Observable.create((observer: Observer<ViewContainer<T>>) => {
      const result = this.query<T>({ ref });

      if (result.length) {
        observer.next(result[0]);
      }

      return this.refChanges
        .filter(propEq('type', 'add'))
        .filter(propEq('ref', ref))
        .map(e => e.container)
        .subscribe(observer);
    });
  }

  query<T>(query: ViewQueryArgs): ViewContainer<T>[] {
    const { ref, id, token } = query;

    if (ref && this._refs.has(ref)) {
      return [ this._refs.get(ref) as ViewContainer<T> ];
    } else if (token) {
      if (this.has(token)) {
        const containers = this.getAll(token) as { [key: number]: ViewContainer<T> };

        if (!id) {
          return Object.keys(containers).map(id => containers[id]);
        }

        for (const containerId of Object.keys(containers)) {
          if (id && containerId === id.toString()) {
            return [ containers[containerId] ];
          }
        }
      }
    }

    return [];
  }

  private _assertAndReadComponent(token: any): ViewComponentConfig {
    const metadata = Reflect.getOwnMetadata(VIEW_CONFIG_KEY, token) as ViewComponentConfig|undefined;

    if (!metadata) {
      throw new Error(`The given token is not a registered ViewComponent.`);
    }

    return metadata;
  }
}

import { ConfiguredRenderable, SerializedRenderable } from '../dom';
import { Inject } from '../di';
import { GenericSerializer, SerializerContainer, Serializer, Serialized } from '../serialization';
import { View } from './View';
import { isBoolean, isNumber } from '../utils';
import { ResolverStrategy, CacheStrategy } from './common';

export interface SerializedView extends SerializedRenderable {
  lazy: boolean|null;
  ref: string|null;
  caching?: CacheStrategy|null;
  token: string;
  useClass: string;
  resolution: ResolverStrategy|null;
}

export class ViewSerializer implements Serializer<View, SerializedView> {
  constructor(
    @Inject(SerializerContainer) private _container: SerializerContainer
  ) {}

  serialize(node: View): SerializedView {
    const token = this._container.resolveClassString(node.token);

    if (!token) {
      throw new Error('Can not serialize view with no registered token.');
    }

    return {
      token,
      name: 'View',
      tags: [ ...node.tags ],
      ref: node.ref,
      caching: node.caching != null ? node.caching : undefined,
      lazy: node.lazy,
      useClass: token,
      resolution: node.resolution
    };
  }

  deserialize(node: SerializedView): ConfiguredRenderable<View> {
    const token = this._container.resolveClass(node.token);
    const useClass = this._container.resolveClass(node.useClass);

    if (!token || !useClass) {
      throw new Error('Could not resolve view classes.');
    }

    return View.configure({
      token, useClass,
      caching: node.caching != null ? node.caching : undefined,
      lazy: isBoolean(node.lazy) ? node.lazy : undefined,
      resolution: isNumber(node.resolution) ? node.resolution : undefined,
      ref: node.ref,
      tags: node.tags
    });
  }

  static register(container: SerializerContainer): void {
    container.registerClass('View', View);
  }
}

import { Token, Type } from '../di';
import { View } from './View';
import { BeforeDestroyEvent } from '../events';
import { Renderable, RenderableConfig } from '../dom';
import { ViewContainer, ViewContainerStatus } from './ViewContainer';
import { LayoutInsertPosition } from '../layout';
import { RenderableArg } from '../common';

export const ViewComponentRef = new Token('ViewComponentRef');
export const LinkerMetatdataRef = new Token('LinkerMetadataRef');
export const VIEW_CONFIG_KEY = 'ugLayout:viewConfig';
export const VIEW_LINKER_METADATA = 'ugLayout:ViewLinkerMetadata';

export enum ResolverStrategy {
  SINGLETON,
  TRANSIENT,
  REF
}

export enum ViewQueryReadType {
  CONTAINER,
  OBSERVABLE,
  COMPONENT  
}

export enum CacheStrategy {
  NONE,
  PERSISTENT,
  RELOAD 
}

export interface ViewQueryArgs {
  ref?: string;
  token?: any;
  id?: number;
}

export interface ViewQueryReadOptions {
  type?: ViewQueryReadType;
  when?: ViewContainerStatus[];
  until?: ViewContainerStatus[];
  lazy?: boolean;
}

export interface ViewResolveConfigArgs {
  query: ViewQueryArgs;
  read?: ViewQueryReadType|ViewQueryReadOptions;
}

export interface ViewResolveConfig extends ViewResolveConfigArgs {
  method: string;
}

export interface ViewLinkerMetadata {
  queries: ViewQueryConfig[];
  inits: ViewQueryInitConfig[];
  inserts: ViewInsertConfig[];
  resolves: ViewResolveConfig[];
}

export interface ViewQueryInitConfig {
  method: string;
  injections: any[];
}

export interface ViewQueryConfigArgs extends ViewQueryArgs {
  read?: ViewQueryReadType|ViewQueryReadOptions;
}

export interface ViewInsertConfigArgs {
  from: ViewQueryArgs;
  into: Type<Renderable>|Type<Renderable>[];
  insert: RenderableArg<Renderable>;
  query: ViewQueryArgs;
  position?: LayoutInsertPosition;
  index?: number;
  read?: ViewQueryReadType|ViewQueryReadOptions;
  tag?: string;
}

export interface ViewInsertConfig extends ViewInsertConfigArgs {
  method: string;
}

export interface ViewQueryConfig {
  ref?: string;
  token?: any;
  id?: number;
  read: ViewQueryReadType|ViewQueryReadOptions;
  method: string;
}

export interface ViewComponentConfig {
  name: string;
  ref: string|null;
  caching: CacheStrategy|null;
  lazy: boolean;
  resolution: ResolverStrategy;
  container: Type<ViewContainer<any>>|null;
}

export type ViewComponentConfigArgs = {
  [P in keyof ViewComponentConfig]?: ViewComponentConfig[P];
}

export interface ViewConfig extends RenderableConfig {
  lazy?: boolean;
  caching?: CacheStrategy|null;
  token?: any;
  ref?: string|null;
  resolution?: ResolverStrategy;
  useFactory?: () => any;
  useValue?: any;
  useClass?: Type<any>;
  deps?: any[];
}

import {
  VIEW_CONFIG_KEY, 
  ViewComponentConfigArgs,
  ResolverStrategy,
  VIEW_LINKER_METADATA,
  ViewQueryConfig,
  ViewQueryConfigArgs,
  ViewQueryReadType,
  ViewQueryInitConfig,
  ViewLinkerMetadata,
  ViewInsertConfigArgs,
  ViewResolveConfigArgs,
  CacheStrategy
} from './common';

/**
 * Registers view metadata with a component class.
 * @export
 * @param {ViewComponentConfigArgs} [config={}] 
 * @returns {ClassDecorator} 
 */
export function ViewComponent(config: ViewComponentConfigArgs = {}): ClassDecorator {
  return (target: Function): void => {
    config = Object.assign({
      caching: null,
      lazy: false,
      resolution: ResolverStrategy.TRANSIENT,
      name: null,
      container: null
    }, config);

    Reflect.defineMetadata(VIEW_CONFIG_KEY, config, target);
  };
}

/**
 * Sets up a view query with the decorated method as the callback to receive the query results. 
 * @export
 * @param {ViewQueryConfigArgs} [config={}] 
 * @returns {MethodDecorator} 
 */
export function ViewQuery(config: ViewQueryConfigArgs = {}): MethodDecorator {
  return (target: Object, key: string) => {
    const metadata: ViewLinkerMetadata = Reflect.getOwnMetadata(VIEW_LINKER_METADATA, target) || getDefaultMetadata();

    metadata.queries.push({
      read: ViewQueryReadType.COMPONENT,
      method: key,
      ...config,
    });

    Reflect.defineMetadata(VIEW_LINKER_METADATA, metadata, target);
  };
}

/**
 * Sets up an init method that is invoked at the time a view is linked by the {@link ViewLinker}. Any dependencies need
 * from the Layouts DI can be injected.
 * @export
 * @param {...any[]} injections Injection tokens for services needing to be injected into the controller.
 * @returns {MethodDecorator} 
 */
export function ViewLinkInit(...injections: any[]): MethodDecorator {
  return (target: Object, key: string) => {
    const metadata: ViewLinkerMetadata = Reflect.getOwnMetadata(VIEW_LINKER_METADATA, target) || getDefaultMetadata();

    metadata.inits.push({
      injections,
      method: key,
    });

    Reflect.defineMetadata(VIEW_LINKER_METADATA, metadata, target);
  };
}

/**
 * Sets up a view insert method.
 * @export
 * @see {@link ViewLinker#wireInsert}
 * @param {ViewInsertConfigArgs} config 
 * @returns {PropertyDecorator} 
 */
export function ViewInsert(config: ViewInsertConfigArgs): PropertyDecorator {
  return (target: Object, key: string) => {
    const metadata: ViewLinkerMetadata = Reflect.getOwnMetadata(VIEW_LINKER_METADATA, target) || getDefaultMetadata();

    metadata.inserts.push({
      ...config,
      method: key
    });

    Reflect.defineMetadata(VIEW_LINKER_METADATA, metadata, target);
  };
}

/**
 * Sets up a view resolve method.
 * @export
 * @see {@link ViewLinker#wireResolve}
 * @param {ViewResolveConfigArgs} config 
 * @returns {PropertyDecorator} 
 */
export function ViewResolve(config: ViewResolveConfigArgs): PropertyDecorator {
  return (target: Object, key: string) => {
    const metadata: ViewLinkerMetadata = Reflect.getOwnMetadata(VIEW_LINKER_METADATA, target) || getDefaultMetadata();

    metadata.resolves.push({
      ...config,
      method: key
    });

    Reflect.defineMetadata(VIEW_LINKER_METADATA, metadata, target);
  };
}

export function getDefaultMetadata(): ViewLinkerMetadata {
  return {
    queries: [],
    inits: [],
    inserts: [],
    resolves: []
  };
}

import { VIEW_HOOK_METADATA, ViewHookMetadata } from './common';
import { isFunction, get } from '../../utils';
import { ViewContainer } from '../ViewContainer';
import { Subject } from '../../events';

/**
 * Executes view hooks on a component instance.
 * @export
 * @class ViewHookExecutor
 */
export class ViewHookExecutor {
  private _interceptors = new WeakMap<any, { [key: string]: Function[] }>();

  /**
   * Invokes the view hook on the instance with the given argument.
   * This will also invoke any interceptors for the hook as well after the hook
   * on the component has been invoked.
   * @template T 
   * @param {T} instance 
   * @param {string} method 
   * @param {*} [arg] 
   * @returns {*} 
   */
  execute<T>(instance: T, method: string, arg?: any): any {
    const target = instance[method];
    let returnValue;

    if (isFunction(target)) {
      returnValue = target.call(instance, arg);
    }

    const interceptorMap = this._interceptors.get(instance) || {};
    const interceptors = interceptorMap[method] || [];

    for (const interceptor of interceptors) {
      interceptor(arg);
    }

    return returnValue;
  }

  /**
   * Registers an interceptor for a hook on a given instance.
   * @param {*} instance 
   * @param {string} hook 
   * @param {Function} fn 
   */
  registerInterceptor(instance: any, hook: string, fn: Function): void {
    const map = this._interceptors.get(instance) || {};
    const list = map[hook] = map[hook] || [];

    list.push(fn);

    this._interceptors.set(instance, map);
  }

  /**
   * Links a resolved view container to any hook observables. Metadata is read from the given target.
   * @template T The component type for the view container.
   * @param {ViewContainer<T>} viewContainer 
   * @param {Object} target 
   */
  linkObservers<T>(viewContainer: ViewContainer<T>, target: Object): void {
    const hooks = Reflect.getOwnMetadata(VIEW_HOOK_METADATA, target) || {};
    const { component } = viewContainer;

    if (!component) {
      return;
    }

    for (const hookObservableName of Object.keys(hooks)) {
      const hookNames = hooks[hookObservableName] || [];
      const subject = new Subject<any>();

      Object.defineProperty(component, hookObservableName, {
        writable: true,
        configurable: true,
        enumerable: true,
        value: subject.asObservable()
      });

      viewContainer.destroyed.subscribe(() => subject.complete());

      for (const name of hookNames) {
        this.registerInterceptor(component, name, arg => subject.next(arg));
      }
    }
  }
}

